INSERT INTO `buku` (`id`, `judul`, `pengarang`, `penerbit`, `tahun`, `quantity`, `gambar`, `id_kategori`, `deskripsi`) VALUES
(1, 'Maryamah Karpov : Mimpi-mimpi Lintang', 'Andrea Hirata', 'Bentang', 2009, 1, 'g18.jpg', 2, 'Maryamah Karpov merupakan penutup dari tetralogi Laskar Pelangi karya Andrea Hirata. Novel ini menceritakan perjuangan Ikal yang kembali ke Belitung setelah menimba ilmu di luar negeri. Ia berusaha mewujudkan mimpi dan cintanya dengan penuh pengorbanan, ditemani kisah tentang sahabat, harapan, serta kegigihan menghadapi kenyataan hidup. Kisah ini sarat pesan tentang persahabatan, cinta, dan semangat meraih mimpi meski penuh rintangan.\r\n'),
(2, 'Pembelajaran Unggul English SMK/MAK Kelas X Jilid B', 'Ikhwan Muslim & Dendy Desmal', 'Bumi Aksara', 2025, 20, 'g131.jpg', 1, 'Buku ini dirancang khusus untuk siswa SMK/MAK kelas X sebagai sarana pembelajaran bahasa Inggris yang komprehensif. Materi disusun sesuai kurikulum terbaru dengan penekanan pada keterampilan listening, speaking, reading, dan writing. Dilengkapi dengan dialog, teks bacaan, latihan soal, serta aktivitas komunikatif yang mendorong siswa aktif berbahasa Inggris dalam konteks sehari-hari maupun dunia kerja.\r\n'),
(3, 'Cerdas Cergas Berbahasa dan Bersastra Indonesia SMA/SMK Kelas X ', 'Fadillah Tri Aulia dan Sefi Indra Gumilar ', 'Pusat Kurikulum dan Perbukuan', 2021, 39, 'g134.jpg', 1, 'Buku ini membantu siswa SMA/SMK kelas X mengembangkan keterampilan berbahasa Indonesia secara lisan maupun tulisan. Materi disajikan secara sistematis meliputi menyimak, berbicara, membaca, menulis, serta apresiasi sastra. Dilengkapi contoh, latihan, dan kegiatan interaktif untuk mendukung pembelajaran aktif dan kreatif.\r\n'),
(4, 'Harry Potter and the Order of The Pheonix', 'JK Rowling', 'PT Gramedia Pustaka Utama', 2004, 1, 'g7.jpg', 2, 'Dalam petualangan kelima ini, Harry Potter menghadapi tahun yang penuh tantangan di Hogwarts. Ia harus melawan tekanan dari Kementerian Sihir, menghadapi guru baru yang kejam, dan membentuk pasukan rahasia untuk melawan kembalinya Voldemort. Kisah ini sarat dengan persahabatan, keberanian, dan perjuangan melawan ketidakadilan.\r\n'),
(5, 'Padang Bulan : Cinta di Dalam Gelas', 'Andrea Hirata', 'PT Bentang Pustaka', 2010, 1, 'g22.jpg', 2, 'Novel ini melanjutkan kisah Enong, gadis kecil dari Belitung yang penuh semangat dalam memperjuangkan hidupnya. Ia menghadapi kerasnya kehidupan dengan tekad kuat untuk meraih pendidikan dan masa depan lebih baik. Melalui cerita yang hangat dan menyentuh, Andrea Hirata menggambarkan perjuangan, cinta, dan keindahan mimpi sederhana di tengah keterbatasan.\r\n'),
(6, 'Bumi Cinta', 'Habiburrahman El Shirazy', 'Author Publishing', 2010, 1, 'g23.jpg', 2, 'Novel ini menceritakan perjalanan Ayyas, seorang mahasiswa Indonesia yang menuntut ilmu di Moskow. Di tengah suasana asing dan godaan kehidupan modern, ia berjuang menjaga iman sekaligus menemukan makna cinta yang sesungguhnya. Kisah ini sarat dengan pesan moral, spiritual, dan kekuatan cinta yang suci.\r\n'),
(7, 'Pemrograman Web SMK Kelas X', 'Heriyanto, S.Kom. dan Gito Rolis, S.kom dan Iky Tri P', 'Yudhistira', 2014, 30, 'g1.jpg', 1, 'Buku ini disusun khusus untuk siswa SMK kelas X sebagai pengantar dasar pemrograman web. Materi mencakup HTML, CSS, serta pengenalan pemrograman berbasis server yang disajikan dengan langkah-langkah praktis. Dilengkapi latihan dan contoh aplikasi sederhana, buku ini membantu siswa memahami konsep sekaligus mempraktikkan pembuatan website.\r\n'),
(8, 'Ensiklopedia Sains Seri Pustaka Vol.5 : Tubuh Kita', 'Tim Penulis', 'Pakar Raya ', 2005, 1, 'g9.jpg', 1, 'Buku ensiklopedia ini menyajikan pengetahuan ilmiah tentang tubuh manusia secara menarik dan mudah dipahami. Dilengkapi ilustrasi, gambar, dan penjelasan sederhana, buku ini membantu pembaca memahami fungsi organ, sistem tubuh, serta pentingnya menjaga kesehatan sejak dini.\r\n'),
(9, 'Ensiklopedia Sains Seri Pustaka Vol.4 : Penciptaan Teknologi', 'Tim Penulis', 'Pakar Raya', 2005, 1, 'g10.jpg', 1, 'Buku ensiklopedia ini membahas perkembangan teknologi dari masa ke masa, mulai dari penemuan sederhana hingga inovasi modern yang mengubah peradaban manusia. Disajikan dengan bahasa yang mudah dipahami dan dilengkapi ilustrasi, buku ini membantu pembaca memahami bagaimana teknologi diciptakan serta dampaknya bagi kehidupan sehari-hari.'),
(10, 'Pendidikan Sosial Budaya', 'Tim Penulis', 'CV Maulana Media Grafika', 2016, 1, 'g10.jpg', 1, 'Buku ini membahas nilai-nilai sosial dan budaya dalam kehidupan masyarakat, serta peran pendidikan dalam melestarikan budaya bangsa.'),
(11, 'Research Methods for Business. Buku 1. Ed 4', 'Uma Sekaran', 'Salemba Empat', 2007, 1, 'g11.jpg', 1, 'Referensi utama untuk memahami metode penelitian bisnis, dengan penjelasan rinci mengenai desain riset, pengumpulan data, dan analisis statistik.'),
(12, 'Research Methods for Business. Buku 2. Ed 4', 'Uma Sekaran', 'Salemba Empat', 2006, 1, 'g12.jpg', 1, 'Lanjutan dari buku pertama, buku ini memberikan contoh kasus nyata penelitian bisnis serta langkah-langkah penerapannya dalam praktik.'),
(13, 'Metode Penelitian untuk Bisnis Buku 1 Ed 6', 'Umar Sekaran, Roger Bougie', 'Salemba Empat', 2017, 1, 'g13.jpg', 1, 'Edisi terbaru dengan pembaruan teori dan metode riset bisnis modern, dilengkapi studi kasus dan panduan praktis.'),
(14, 'Sila Ke-6: Kreatif Sampai Mati!', 'Wahyu Aditya', 'PT Bentang Pustaka', 2013, 1, 'g14.jpg', 1, 'Buku inspiratif yang mengajak pembaca untuk berpikir kreatif tanpa batas, dengan banyak contoh ide unik dari dunia industri kreatif Indonesia.'),
(15, 'Akuntansi Pajak Ed 6', 'Waluyo', 'Salemba Empat', 2016, 1, 'g200.jpg', 1, 'Panduan komprehensif mengenai akuntansi pajak di Indonesia, disesuaikan dengan peraturan perpajakan terbaru.'),
(16, 'Teknik Kendaraan Ringan SMK/MAK Kelas XI', 'Sutrisno', 'Bumi Aksara', 2025, 21, 'g202.jpg', 1, 'Buku ajar untuk siswa SMK/MAK yang membahas dasar-dasar perawatan dan perbaikan kendaraan ringan, dilengkapi ilustrasi teknis dan latihan soal.'),
(17, 'Akuntansi SMK/MAK Kelas XI', 'Riris Khuzaemah', 'Bumi Aksara', 2025, 1, 'g11.jpg', 1, 'Buku ini menyajikan materi akuntansi untuk siswa SMK/MAK kelas XI secara sistematis, membantu memahami konsep dasar pencatatan, laporan keuangan, hingga analisis data akuntansi.'),
(18, 'Teknik Komputer Jaringan SMK/MAK Kelas XI', 'Darsono', 'Bumi Aksara', 2025, 20, 'g203.jpg', 1, 'Buku ini membahas dasar-dasar jaringan komputer untuk siswa SMK/MAK kelas XI, meliputi instalasi, konfigurasi, pemeliharaan, dan troubleshooting jaringan, sesuai kebutuhan pembelajaran praktik.'),
(19, 'Sang Pemimpi', 'Andrea Hirata', 'PT Bentang Pustaka', 2008, 1, 'g223.jpg', 2, 'Novel tersebut adalah \"Sang Pemimpi\" karya Andrea Hirata, yang merupakan lanjutan dari Laskar Pelangi. Buku ini menceritakan kisah Ikal, Arai, dan Jimbron, tiga sahabat dari Belitung yang memiliki impian besar untuk menaklukkan dunia meski hidup dalam keterbatasan ekonomi. Dengan latar kehidupan di masa remaja hingga dewasa awal, novel ini menggambarkan perjuangan mereka menempuh pendidikan, bekerja keras, dan tetap berpegang pada mimpi untuk belajar ke luar negeri. Arai, yang dijuluki “Simpai Keramat”, menjadi simbol optimisme dan semangat pantang menyerah, sementara Ikal sebagai narator menuturkan kisahnya dengan penuh humor, kehangatan, dan refleksi sosial. Melalui perjalanan mereka, Andrea Hirata menonjolkan nilai persahabatan, cinta, keluarga, dan keyakinan bahwa mimpi sebesar apa pun bisa dicapai dengan tekad dan doa.'),
(20, 'Perahu Kertas', 'Dee', 'PT Bentang Pustaka', 2009, 1, 'g201.jpg', 2, 'Novel populer karya Dewi Lestari tentang kisah cinta Kugy dan Keenan yang penuh lika-liku, dipadukan dengan mimpi dan seni.'),
(21, 'Serial Syakila : Bom!', 'Fahri Asiza', 'DAR! Mizan', 2003, 1, 'g34.jpg', 2, 'Bagian dari Serial Syakila ini sarat dengan konflik khas remaja yang dipenuhi gejolak persahabatan, cinta, dan dinamika kehidupan sehari-hari. Cerita menggambarkan bagaimana Syakila dan tokoh-tokoh di sekitarnya menghadapi masa transisi penuh kegelisahan, mulai dari pertentangan batin, kesetiaan yang diuji, hingga manis-pahitnya cinta pertama. Dengan gaya penceritaan yang dekat dengan realitas remaja, novel ini menyuguhkan perjalanan penuh emosi yang tak hanya menghibur, tetapi juga menyampaikan pesan tentang arti persahabatan sejati, keberanian mengambil keputusan, serta pentingnya tetap teguh pada jati diri di tengah perubahan hidup.'),
(22, 'Serial Syakila : Dia! Terungkapnya Si Tuan Besar', 'Fahri Asiza', 'DAR! Mizan', 2004, 1, 'g32.jpg', 2, 'Salah satu seri Syakila ini menghadirkan kisah penuh misteri dan intrik, ketika tokoh utama harus berhadapan dengan rahasia besar yang selama ini tersembunyi dan mengancam kestabilan hidupnya. Setiap lembar cerita membawa pembaca ke dalam ketegangan, teka-teki, serta pergulatan batin yang membuat Syakila terjebak antara kebenaran, pengkhianatan, dan keberanian untuk mengungkap fakta yang sesungguhnya. Dengan alur yang penuh kejutan dan dinamika emosional, novel ini menegaskan bahwa di balik setiap rahasia selalu ada harga yang harus dibayar, namun juga membuka ruang bagi kekuatan dan keberanian untuk menghadapi kenyataan.'),
(23, 'Serial Syakila : Elegi!', 'Fahri Asiza', 'DAR! Mizan', 2003, 1, 'g27.jpg', 2, 'Serial Syakila mengisahkan perjalanan hidup seorang perempuan bernama Syakila yang harus menghadapi dilema antara keinginan pribadi, harapan keluarga, dan realita sosial yang penuh tantangan. Dengan alur yang sarat konflik batin, persahabatan, cinta, dan pencarian jati diri, novel ini menghadirkan kisah emosional yang menyentuh sekaligus memberikan pesan moral tentang kesabaran, doa, serta keberanian dalam memilih jalan hidup menuju kebahagiaan sejati.'),
(24, 'Serial Syakila : Puing!', 'Fahri Asiza', 'DAR! Mizan', 2004, 1, 'g31.jpg', 2, 'Salah satu bagian dari Serial Syakila ini menghadirkan kisah yang penuh drama dan ketegangan, ketika tokoh utama harus menghadapi kehancuran hidup yang mengguncang jiwa serta meruntuhkan segala yang pernah ia yakini. Namun di balik perih dan luka, terselip secercah harapan baru yang perlahan tumbuh, menjadi kekuatan untuk bangkit dan melangkah kembali. Dengan alur yang intens dan emosional, novel ini menggambarkan bagaimana setiap kejatuhan dapat membuka jalan menuju pembaruan, serta menyampaikan pesan tentang ketabahan, harapan, dan keberanian untuk merajut kembali kehidupan dari kepingan yang hancur.'),
(25, 'Serial Syakila : Retak!', 'Fahri Asiza', 'DAR! Mizan', 2004, 1, 'g33.jpg', 2, 'Novel ini, sebagai bagian dari Serial Syakila, menghadirkan konflik emosional yang mendalam dengan fokus pada retaknya hubungan dalam kehidupan tokoh utama. Pertentangan batin, kesalahpahaman, dan luka hati menjadi rangkaian peristiwa yang mengguncang fondasi hubungan yang selama ini ia jalani. Di tengah kerapuhan dan kehilangan kepercayaan, Syakila dituntut untuk memilih antara mempertahankan yang telah hancur atau merelakan demi menemukan kedamaian baru. Dengan narasi yang menyentuh dan penuh nuansa emosional, kisah ini menyingkap sisi rapuh manusia sekaligus memberikan pesan tentang keikhlasan, pengampunan, dan arti sejati dari sebuah hubungan.'),
(26, 'Serial Syakila : Topeng!', 'Fahri Asiza', 'DAR! Mizan', 2004, 1, 'g.jpg', 2, 'Seri Syakila yang menggambarkan kehidupan penuh kepalsuan dan rahasia, membuka tabir jati diri yang sesungguhnya.'),
(27, 'Serial Syakila : Tragedi!', 'Fahri Asiza', 'DAR! Mizan', 2003, 1, 'g29.jpg', 2, 'Bagian dari Serial Syakila ini sarat dengan tragedi yang mengguncang perjalanan hidup tokoh utama, menyingkap sisi emosional terdalam ketika ia harus menghadapi kenyataan pahit yang tak terelakkan. Diuji oleh kehilangan, pengkhianatan, dan luka batin yang dalam, Syakila berjuang menjaga kekuatan dirinya di tengah kerapuhan jiwa. Novel ini menyoroti pergulatan antara rasa sakit dan harapan, memperlihatkan bagaimana duka bisa menjadi jalan menuju kedewasaan, serta menyampaikan pesan bahwa di balik setiap tragedi selalu ada ruang untuk bangkit dan menemukan arti baru dari kehidupan.'),
(28, 'Rentang Kisah', 'Gita Savitri Devi', 'Gagas Media', 2017, 1, 'g193.jpg', 2, 'Memoar inspiratif karya Gita Savitri Devi ini menghadirkan kisah perjalanan hidupnya mulai dari pengalaman masa muda, pendidikan di luar negeri, hingga lika-liku proses pendewasaan diri. Dengan gaya bercerita yang jujur dan reflektif, ia berbagi pandangannya tentang berbagai isu penting seperti agama, feminisme, pernikahan, hingga pilihan hidup yang kerap memicu perdebatan publik. Buku ini tidak hanya menampilkan sisi personal Gita, tetapi juga menyajikan refleksi kritis yang mengajak pembaca untuk berpikir lebih terbuka, berani mengambil keputusan, dan tetap setia pada nilai yang diyakini.'),
(29, 'Habibie 2 Kecil Tapi Otak Semua', 'A Makmur Makka', 'Edelweis', 2011, 1, 'g185.jpg', 2, 'Novel biografi inspiratif tentang sosok Habibie di masa kecil yang dikenal cerdas dan penuh semangat belajar, menjadi teladan bagi generasi muda.'),
(30, 'Sabtu Bersama Bapak', 'Adhitya Mulya', 'Gagas Media', 2014, 1, 'g.jpg', 2, 'Sebuah novel keluarga yang mengisahkan pesan seorang ayah kepada anak-anaknya melalui video, penuh dengan nilai kehidupan dan cinta.'),
(31, 'Edensor', 'Andrea Hirata', 'PT Bentang Pustaka', 2008, 1, 'g207.jpg', 2, 'Novel ketiga dari tetralogi Laskar Pelangi ini mengisahkan petualangan Ikal dan Arai dalam mengejar mimpi menuntut ilmu di Eropa. Di tengah keterbatasan, mereka berjuang menghadapi berbagai tantangan hidup di negeri orang, merasakan manis pahitnya perjuangan, dan menemukan arti cinta serta persahabatan yang sesungguhnya. Dengan gaya penceritaan yang hangat dan penuh humor khas Andrea Hirata, novel ini menyuguhkan kisah inspiratif tentang tekad, pengorbanan, dan keyakinan bahwa mimpi besar bisa diraih dengan keberanian dan kerja keras.'),
(32, 'Erlangga X-Press UN SMK/MAK 2020 Bahasa Inggris', 'Endrawati dan H. Genderia Sari', 'Penerbit Erlangga', 2019, 3, 'g204.jpg', 1, 'Buku latihan soal Bahasa Inggris khusus untuk persiapan Ujian Nasional SMK/MAK 2020, disusun dengan pembahasan lengkap dan strategi pengerjaan soal.'),
(33, 'Bumi Cinta', 'Habiburrahman El Shirazy', 'Author Publishing', 2010, 1, 'g.jpg', 2, 'Novel yang mengisahkan perjalanan seorang mahasiswa Indonesia di Rusia yang harus menghadapi berbagai ujian iman, cinta, dan kehidupan.'),
(34, 'Cinta Suci Zahrana', 'Habiburrahman El Shirazy', 'Ihwah Publishing House', 2012, 1, 'g234.jpg', 2, 'Novel ini berjudul “Ketika Cinta Bertasbih: Dalam Mihrab Cinta – Mencari Zahrana” karya Habiburrahman El Shirazy, yang mengisahkan perjalanan hidup Zahrana, seorang perempuan cerdas, mandiri, dan berprestasi yang berprofesi sebagai dosen di sebuah universitas ternama. Meskipun sukses secara akademik dan karier, Zahrana harus menghadapi tekanan sosial karena belum menikah di usia matang, ditambah pandangan miring masyarakat yang sering menilai perempuan dari status pernikahan. Dalam perjuangannya mempertahankan prinsip dan harga diri, Zahrana dihadapkan pada ujian berat yang mengguncang keyakinannya, termasuk pengkhianatan, fitnah, dan cobaan cinta. Melalui kisahnya, novel ini menyoroti keteguhan iman, kesucian diri, dan keberanian seorang wanita muslimah dalam menegakkan nilai-nilai moral dan kehormatan di tengah tuntutan dunia modern. Ceritanya sarat pesan moral tentang keikhlasan, kesabaran, serta keyakinan bahwa setiap ujian membawa hikmah dan jalan menuju kebahagiaan sejati.'),
(35, 'Perkamen Rahasia Harry Potter', 'Hief Skeeter', 'Grafindo Litera Media', 2006, 1, 'g.jpg', 2, 'Buku yang mengulas rahasia dan teori di balik dunia sihir Harry Potter, ditulis untuk penggemar yang ingin mengetahui lebih dalam tentang kisah JK Rowling.'),
(36, 'Nyanyian Bintang : Kisah Dua Anak Manusia', 'Ifa Avianty', 'PT Syaamil Cipta Media', 2005, 1, 'g38.jpg', 2, 'Novel islami ini mengisahkan perjuangan dua anak manusia dalam menemukan arti cinta sejati yang tidak hanya berlandaskan perasaan, tetapi juga iman dan pengorbanan. Dengan latar kehidupan yang sarat nilai religius, keduanya menghadapi berbagai ujian, mulai dari godaan duniawi, perbedaan prinsip, hingga pengorbanan besar demi menjaga kesucian cinta sesuai tuntunan agama. Melalui perjalanan penuh ujian tersebut, pembaca diajak memahami bahwa cinta sejati bukan sekadar rasa memiliki, melainkan sebuah perjalanan spiritual untuk saling menuntun menuju kebaikan, keteguhan iman, dan ridha Allah.'),
(37, 'In A Blue Moon', 'Ilana Tan', 'PT Gramedia Pustaka Utama', 2015, 1, 'g54.jpg', 2, 'Novel romantis ini mengisahkan pertemuan tak terduga antara dua insan yang datang dari latar belakang berbeda, di mana perbedaan itu semula menimbulkan kesalahpahaman dan jarak di antara mereka. Namun, melalui interaksi sehari-hari, perdebatan kecil, dan momen-momen sederhana, benih cinta perlahan tumbuh tanpa mereka sadari. Cerita ini menyajikan perjalanan emosional yang hangat, memperlihatkan bagaimana cinta mampu melampaui perbedaan, menyatukan hati yang sebelumnya bertolak belakang, serta memberikan makna baru tentang pengertian, penerimaan, dan keberanian membuka diri pada perasaan yang tulus.'),
(38, 'Summer In Seoul', 'Ilana Tan', 'PT Gramedia Pustaka Utama', 2010, 1, 'g57.jpg', 2, 'Novel ini mengisahkan kisah cinta yang terjalin di Seoul antara seorang mahasiswa Indonesia dan seorang penyanyi Korea yang tengah berada di puncak popularitas. Pertemuan mereka yang tak terduga berkembang menjadi hubungan yang penuh kehangatan, namun juga diwarnai dengan perbedaan budaya, pandangan hidup, serta tekanan dari lingkungan sekitar. Di balik romansa yang manis, keduanya harus menghadapi tantangan besar berupa jarak, ekspektasi keluarga, dan sorotan publik yang kerap mengguncang perasaan. Dengan alur yang romantis sekaligus emosional, kisah ini menyampaikan pesan bahwa cinta sejati membutuhkan pengertian, pengorbanan, dan keberanian untuk melawan segala perbedaan demi kebahagiaan bersama.'),
(39, 'Sunshine Becomes You', 'Ilana Tan', 'PT Gramedia Pustaka Utama', 2012, 1, 'g58.jpg', 2, 'Novel romantis ini mengisahkan pertemuan tak terduga antara seorang pianis terkenal yang hidup dalam gemerlap dunia musik dengan seorang gadis sederhana yang penuh ketulusan. Dari interaksi singkat yang berlanjut menjadi kebersamaan, keduanya perlahan menemukan makna baru tentang hidup, cinta, dan kebahagiaan yang sejati. Sang pianis belajar melihat dunia di luar panggung yang selama ini membelenggunya, sementara sang gadis menemukan keberanian untuk bermimpi lebih besar. Kisah ini menghadirkan perjalanan emosional yang manis sekaligus mengharukan, menunjukkan bahwa cinta mampu mengubah cara pandang seseorang terhadap kehidupan dan memberi arti baru pada setiap nada yang dimainkan.'),
(40, 'Watir', 'Infowatir', 'RakBuku', 2012, 1, 'g205.jpg', 2, 'Sebuah karya fiksi yang menghadirkan potret kehidupan masyarakat dengan segala problematika dan dinamika sosialnya. Melalui gaya penceritaan yang lugas, penulis mengungkapkan realita sehari-hari, mulai dari kesenjangan, perjuangan hidup, hingga harapan sederhana yang sering terabaikan. Kisah ini tidak hanya menjadi cermin bagi pembaca untuk melihat persoalan sosial di sekitar, tetapi juga sarat makna dan renungan tentang nilai kemanusiaan serta pentingnya kepedulian terhadap sesama.'),
(41, 'Chicken Soup for the Woman\'s Soul', 'Jack Canfield', 'PT Gramedia Pustaka Utama', 2011, 1, 'g214.jpg', 2, 'Buku ini merupakan kumpulan kisah inspiratif yang ditujukan untuk para wanita, berisi cerita-cerita nyata tentang cinta, kekuatan, dan keberanian dalam menghadapi tantangan hidup. Setiap kisah menyoroti perjuangan perempuan dalam berbagai peran—sebagai anak, istri, ibu, maupun individu yang berjuang meraih impian—serta bagaimana mereka menemukan makna dari setiap ujian. Dengan bahasa yang menyentuh hati dan penuh motivasi, buku ini menghadirkan potret keteguhan hati wanita sekaligus menjadi sumber semangat dan refleksi bagi pembaca.'),
(42, 'The 100 Year Old Man Who Climbed Out Of The Window And Disappeared', 'Jonas Jonasson', 'PT Bentang Pustaka', 2013, 1, 'g.jpg', 2, 'Kisah kocak tentang seorang pria berusia 100 tahun yang kabur dari panti jompo dan terlibat dalam petualangan penuh humor dan intrik.'),
(43, 'Love, Curse, & Hocus-Pocus', 'Karla M. Nashar', 'PT Gramedia Pustaka Utama', 2013, 1, 'g208.jpg', 2, 'Novel romantis ini memadukan kisah cinta yang hangat dengan misteri yang penuh teka-teki, dihiasi sentuhan humor segar serta kehadiran sihir dalam kehidupan sehari-hari. Alurnya menghadirkan kejutan-kejutan tak terduga, di mana hubungan antar tokoh berkembang di tengah rahasia yang perlahan terungkap. Perpaduan antara romantisme, intrik, dan elemen magis membuat cerita ini terasa unik, menghibur, sekaligus menyentuh hati, menghadirkan bacaan yang ringan namun tetap memikat dari awal hingga akhir.'),
(44, 'Love, Hate, & Hocus-Pocus', 'Karla M. Nashar', 'PT Gramedia Pustaka Utama', 2012, 1, 'g59.jpg', 2, 'Novel ini merupakan lanjutan kisah penuh drama dan cinta yang dipadukan dengan bumbu fantasi, menghadirkan konflik perasaan yang intens sekaligus sentuhan keajaiban yang memikat. Di balik lika-liku hubungan antar tokoh yang dipenuhi keraguan, pengorbanan, dan rasa rindu, hadir pula elemen magis yang memberi warna baru dalam perjalanan mereka. Perpaduan antara realita dan fantasi menjadikan cerita ini semakin emosional dan tak terduga, mengajak pembaca larut dalam suasana romantis yang dramatis sekaligus menegangkan. Sebuah kisah yang menyentuh hati, menghadirkan pesan tentang kekuatan cinta dan harapan di tengah segala ujian.'),
(45, 'The Bliss Bakery Trilogy #2: Dash Of Magic', 'Kathryn Littlewood', 'Noura', 2012, 1, 'g.jpg', 2, 'Petualangan keluarga Bliss berlanjut dengan resep ajaib yang membawa mereka ke situasi berbahaya sekaligus penuh keajaiban.'),
(46, 'The Bliss Bakery Trilogy #3: Bite-Sized Magic', 'Kathryn Littlewood', 'Mizan Fantasi', 2014, 1, 'g.jpg', 2, 'Buku ketiga dari seri Bliss Bakery yang penuh dengan humor, sihir, dan tantangan untuk menjaga warisan keluarga mereka.'),
(47, 'Quidditch Through the Ages', 'Kennilworthy Whisp', 'PT Gramedia Pustaka Utama', 2006, 1, 'g.jpg', 2, 'Buku fiksi dari dunia Harry Potter yang menjelaskan sejarah, aturan, dan keunikan olahraga sihir Quidditch.'),
(48, 'Tabi', 'Marchella FP', 'PT Kebahagiaan Itu Sederhana', NULL, 1, 'g220.jpg', 2, 'Buku ini merupakan karya inspiratif dengan ilustrasi dan narasi sederhana yang mengajak pembaca merenungkan perjalanan hidup. Setiap halaman menghadirkan pesan singkat namun bermakna, disertai gambar yang memperkuat suasana, sehingga mudah dipahami oleh semua kalangan. Dengan gaya yang ringan namun menyentuh, buku ini membantu pembaca menemukan makna dalam hal-hal kecil, menghargai momen, serta belajar melihat kehidupan dengan lebih bijaksana dan penuh rasa syukur.'),
(49, 'Semua Sayang Kamu', 'Mas Harto', 'DAR! Mizan', 2005, 1, 'g37.jpg', 2, 'Novel remaja ini menghadirkan kisah penuh warna tentang cinta, persahabatan, dan perjuangan anak muda dalam menghadapi berbagai tantangan hidup. Dengan gaya penulisan yang ringan dan dekat dengan keseharian, cerita menggambarkan dinamika remaja yang mencari jati diri, menghadapi konflik perasaan, serta belajar arti kesetiaan dalam persahabatan. Di balik keceriaan masa muda, terselip pula perjuangan untuk meraih mimpi dan keberanian mengambil keputusan penting. Kisah ini tidak hanya menghibur, tetapi juga memberikan pesan inspiratif tentang arti cinta sejati, kekuatan persahabatan, dan semangat pantang menyerah dalam menjalani kehidupan.'),
(50, 'Kisah 25 Nabi dan Rasul', 'MB. Rahimsyah AR', 'Putra Harsa', NULL, 1, 'g235.jpg', 2, 'Buku ini menyajikan kisah-kisah inspiratif dari 25 nabi dan rasul yang ditulis dengan bahasa sederhana, menarik, dan mudah dipahami oleh pembaca segala usia. Setiap kisah menggambarkan perjalanan hidup para nabi dalam menghadapi ujian, menyebarkan kebenaran, serta menegakkan keimanan kepada Allah SWT. Melalui cerita-cerita yang penuh hikmah, pembaca diajak untuk meneladani kesabaran Nabi Ayyub, keteguhan Nabi Nuh, kebijaksanaan Nabi Sulaiman, hingga perjuangan Nabi Muhammad SAW. Selain memberikan wawasan sejarah keislaman, buku ini juga menanamkan nilai-nilai moral dan spiritual yang relevan untuk kehidupan modern. Dengan ilustrasi yang menarik dan penyajian yang ringan, buku ini menjadi bacaan ideal bagi anak-anak, remaja, maupun orang dewasa yang ingin memperdalam pengetahuan tentang para nabi dan mengambil pelajaran dari keteladanan mereka.'),
(51, 'The Secrets of the Immortal Nicholas Flamel : The Alchemyst', 'Michael Scott', 'Matahati', 2009, 1, 'g.jpg', 2, 'Awal dari seri fantasi Nicholas Flamel, bercerita tentang perebutan buku sihir kuno yang dapat menyelamatkan atau menghancurkan dunia.'),
(52, 'The Secrets of the Immortal Nicholas Flamel : The Magician', 'Michael Scott', 'Matahati', 2009, 1, 'g.jpg', 2, 'Petualangan berlanjut dengan ancaman baru dari penyihir hebat dan perjuangan mempertahankan keseimbangan dunia.'),
(53, 'The Secrets of the Immortal Nicholas Flamel : The Necromancer', 'Michael Scott', 'Matahati', 2011, 1, 'g.jpg', 2, 'Bagian keempat seri Flamel dengan pertarungan semakin sengit antara kekuatan gelap dan terang.'),
(54, 'The Secrets of the Immortal Nicholas Flamel : The Sorceress', 'Michael Scott', 'Matahati', 2010, 1, 'g.jpg', 2, 'Nicholas Flamel menghadapi penyihir hebat yang menguji keberanian dan kesetiaan dua anak kembar terpilih.'),
(55, 'The Secrets of the Immortal Nicholas Flamel : The Warlock', 'Michael Scott', 'Matahati', 2012, 1, 'g.jpg', 2, 'Buku kelima seri Flamel yang menegangkan, penuh konflik magis dan intrik kekuasaan.'),
(56, 'The Time Keeper', 'Mitch Albom', 'PT Gramedia Pustaka Utama', 2014, 1, 'g20.jpg', 2, 'The Time Keeper adalah sebuah novel inspiratif karya Mitch Albom yang mengisahkan tentang Father Time, manusia pertama yang menghitung waktu dan kemudian dihukum karena obsesinya. Ia dipaksa untuk mendengarkan keluhan manusia tentang waktu hingga akhirnya diberi kesempatan menebus kesalahannya dengan membantu dua orang: seorang remaja putri yang putus asa ingin mengakhiri hidupnya dan seorang pria kaya raya yang berambisi memperpanjang usianya. Melalui perjalanan ini, novel menggambarkan makna sejati waktu, bagaimana manusia sering menyia-nyiakannya, serta pentingnya mensyukuri setiap detik kehidupan. Dengan gaya penceritaan yang menyentuh dan penuh renungan, Mitch Albom mengajak pembaca merenungkan hubungan antara waktu, kehidupan, dan makna kebahagiaan sejati.'),
(57, 'Tuesday With Morrie', 'Mitch Albom', 'PT Gramedia Pustaka Utama', 2014, 1, 'g238.jpg', 2, 'Buku ini merupakan memoar inspiratif tentang pertemuan penulis dengan dosen lamanya, yang secara tak terduga kembali hadir di penghujung hidup sang guru. Melalui serangkaian pertemuan yang hangat dan penuh makna, penulis belajar tentang arti kehidupan, cinta, kerja keras, dan penerimaan terhadap kematian. Setiap dialog antara keduanya sarat dengan nilai-nilai kemanusiaan, kebijaksanaan, dan refleksi mendalam tentang makna hidup yang sesungguhnya. Kisah ini tidak hanya menggambarkan hubungan seorang murid dan gurunya, tetapi juga perjalanan spiritual seseorang dalam memahami pentingnya kasih, empati, dan waktu yang terbatas di dunia. Dengan gaya penceritaan yang menyentuh dan reflektif, buku ini mengajarkan bahwa pelajaran terbaik sering kali datang dari hati yang tulus dan perpisahan yang tak terelakkan.'),
(58, 'Rembulan Jatuh', 'Muhammad Saidi', 'DAR! Mizan', 2005, 1, 'g28.jpg', 2, 'Novel ini menggambarkan perjalanan hidup seorang tokoh yang dipenuhi lika-liku cinta, harapan, dan mimpi, serta dinamika emosional yang menyertainya. Setiap langkah cerita menyoroti perjuangan meraih kebahagiaan, menghadapi kekecewaan, hingga menemukan makna sejati dari cinta dan impian yang diperjuangkan. Dengan alur yang menyentuh dan penuh kejutan, kisah ini mengajak pembaca untuk merenungkan arti pengorbanan, kekuatan hati, dan keyakinan bahwa setiap ujian dalam hidup adalah bagian dari proses menuju kedewasaan dan kebahagiaan sejati.'),
(59, 'Our Story', 'Orizuka', 'Authorized Books', 2010, 1, 'g51.jpg', 2, 'Novel remaja ini mengisahkan perjalanan hidup anak muda yang penuh warna, di mana persahabatan dan cinta tumbuh berdampingan dalam dinamika keseharian. Tokoh-tokohnya digambarkan dengan karakter yang hangat dan dekat dengan realitas remaja, menghadapi suka duka bersama, saling mendukung, sekaligus belajar dari konflik yang muncul. Dengan alur yang ringan namun menyentuh, cerita ini menyoroti bagaimana persahabatan dapat menjadi landasan kuat bagi tumbuhnya cinta, serta bagaimana cinta remaja memberi pelajaran tentang pengertian, kesetiaan, dan keberanian menghadapi perubahan hidup. Novel ini menghadirkan kisah inspiratif yang tak hanya menghibur, tetapi juga menyentuh hati pembaca muda.'),
(60, 'Dilan : Dia Adalah Dilanku tahun 1990', 'Pidi Baiq', 'DAR! Mizan', 2014, 1, 'g209.jpg', 2, 'Novel populer ini mengisahkan perjalanan cinta remaja antara Milea dan Dilan dengan latar Bandung tahun 1990. Dilan, seorang remaja dengan gaya unik, cerdas, sekaligus nakal, berhasil menarik perhatian Milea dengan cara-cara yang berbeda dari kebanyakan orang. Kisah mereka dipenuhi romansa manis, konflik khas remaja, serta dinamika persahabatan dan keluarga yang membentuk kehidupan masa muda. Dengan nuansa nostalgia era 90-an, novel ini menghadirkan cerita sederhana namun mengena, yang membuat pembaca hanyut dalam kenangan cinta pertama yang hangat dan penuh warna.'),
(61, 'Dilan : Dia Adalah Dilanku tahun 1991', 'Pidi Baiq', 'Pastel Books', 2015, 1, 'g211.jpg', 2, 'Novel ini merupakan lanjutan kisah cinta Milea dan Dilan yang ber-setting di Bandung tahun 1991. Cerita menghadirkan nuansa nostalgia masa remaja yang penuh warna, dibalut dengan humor khas Dilan yang jenaka sekaligus romantis. Hubungan keduanya semakin diuji oleh perbedaan, kecemburuan, serta dinamika pergaulan anak muda kala itu, namun tetap dipenuhi kehangatan yang mengingatkan pada indahnya masa muda. Dengan gaya narasi yang ringan dan mengalir, novel ini menyuguhkan kisah cinta sederhana namun berkesan, yang lekat dengan kenangan manis pembaca terhadap masa SMA.'),
(62, 'Ulysses Moore : Peta Yang Hilang', 'Pierdomenico Baccalario', 'Erlangga For Kids', 2005, 1, 'g.jpg', 2, 'Petualangan seru Ulysses Moore mencari peta misterius yang membuka pintu ke dunia penuh rahasia.'),
(63, 'Ulysses Moore : Rumah Cermin', 'Pierdomenico Baccalario', 'Erlangga For Kids', 2005, 1, 'g.jpg', 2, 'Melanjutkan petualangan menegangkan, kali ini di rumah cermin yang menyimpan rahasia besar.'),
(64, 'Ubur-ubur Lembur', 'Raditya Dika', 'PT Gramedia Pustaka Utama', 2018, 1, 'g174.jpg', 2, 'Buku ini merupakan kumpulan cerita humor karya Raditya Dika yang siap mengocok perut pembaca, diangkat dari pengalaman hidup sehari-hari yang dikemas dengan gaya jenaka khas penulis. Melalui kejadian-kejadian sederhana, mulai dari kisah persahabatan, cinta, hingga kegagalan konyol dalam keseharian, Raditya Dika berhasil menyajikan humor segar yang dekat dengan realitas anak muda. Setiap cerita tidak hanya menghadirkan tawa, tetapi juga refleksi ringan tentang kehidupan modern dengan segala dinamika dan keabsurditasnya. Bacaan ini cocok untuk siapa saja yang ingin terhibur sekaligus merasa akrab dengan kisah-kisah kocak yang penuh kejujuran.'),
(65, 'Kening', 'Rakhmawati Fitri', 'Terrant', 2011, 1, 'g219.jpg', 2, 'Novel ini ditulis dengan gaya puitis dan emosional, mengangkat tema cinta, kehilangan, dan pencarian jati diri yang mendalam. Kisahnya berpusat pada tokoh utama yang harus menghadapi pahitnya kehilangan orang terdekat, lalu menapaki perjalanan batin untuk menemukan arti cinta sejati sekaligus jati dirinya. Dengan alur penuh refleksi dan bahasa yang indah, novel ini tidak hanya menghadirkan kisah romantis, tetapi juga renungan filosofis tentang luka, harapan, dan proses penyembuhan hati yang mampu menyentuh perasaan pembaca.'),
(66, 'Jadian 6 Bulan', 'Rhein Fathia', 'DAR! Mizan', 2005, 1, 'g35.jpg', 2, 'Novel ini mengisahkan kisah remaja tentang sebuah hubungan cinta kontrak selama enam bulan yang awalnya dijalani hanya sebagai kesepakatan sementara, namun perlahan membawa banyak kejutan yang tak terduga. Dari interaksi yang canggung, pertengkaran kecil, hingga munculnya perasaan yang tulus, kisah ini menyajikan dinamika hubungan yang manis sekaligus penuh konflik. Dengan gaya penceritaan yang ringan dan dekat dengan keseharian remaja, novel ini menyoroti bagaimana cinta bisa hadir di luar dugaan, serta mengajarkan bahwa setiap pertemuan memiliki makna yang dapat mengubah cara pandang seseorang terhadap cinta dan kehidupan.'),
(67, 'Trilogi Soekram', 'Sapardi Djoko Damono', 'PT Gramedia Pustaka Utama', 2015, 1, 'g.jpg', 2, 'Kumpulan novel eksperimental karya Sapardi yang penuh satire, kritik sosial, dan refleksi kehidupan.'),
(68, 'Kekuatan Ketujuh', 'Sinta Yudisia', 'Beranda', 2004, 1, 'g222.jpg', 2, 'Novel motivasi ini menyajikan kisah inspiratif tentang perjuangan tokoh utama dalam menemukan kekuatan dalam dirinya. Berawal dari keterpurukan dan rasa tidak percaya diri, ia melalui berbagai rintangan hidup yang menguji mental, hati, dan tekad. Dengan semangat pantang menyerah serta dukungan dari orang-orang di sekitarnya, perjalanan itu menuntunnya pada kesadaran bahwa kekuatan sejati berasal dari keyakinan, usaha, dan keberanian untuk bangkit. Ditulis dengan alur yang menyentuh dan penuh makna, novel ini menghadirkan motivasi bagi pembaca untuk terus berjuang menghadapi tantangan hidup.'),
(69, 'Berjuta Rasanya', 'Tere Liye', 'Mahaka Publishing', 2016, 1, 'g215.jpg', 2, 'Buku ini berisi kumpulan cerita pendek yang menyentuh hati, mengangkat tema cinta, persahabatan, dan kehidupan dengan nuansa sederhana namun penuh makna. Setiap kisah menghadirkan potret pengalaman manusia yang dekat dengan keseharian, mulai dari manisnya cinta pertama, hangatnya persahabatan sejati, hingga pelajaran berharga dari kehilangan dan harapan. Ditulis dengan bahasa yang ringan dan emosional, buku ini mampu menggugah perasaan pembaca sekaligus memberikan refleksi tentang arti kebersamaan, kasih sayang, dan perjalanan hidup.'),
(70, 'Pulang', 'Tere Liye', 'Republika', 2015, 1, 'g21.jpg', 2, 'Novel ini penuh dengan intrik dan konflik batin, mengisahkan perjalanan hidup seorang anak desa yang polos dan sederhana hingga akhirnya terjerumus ke dalam dunia gelap penuh tipu daya. Dari masa kecil yang penuh harapan, ia perlahan terbawa arus kerasnya kehidupan kota, dihadapkan pada pilihan-pilihan sulit yang menguji moral dan nuraninya. Intrik kekuasaan, persahabatan yang berbalut pengkhianatan, serta ambisi yang tak terkendali menjadikan kisah ini sarat dengan ketegangan emosional. Novel ini tidak hanya menggambarkan sisi kelam perjalanan hidup, tetapi juga menyingkap bagaimana seseorang tetap bisa mencari cahaya meski terperangkap dalam kegelapan.'),
(71, 'Rembulan Tenggelam Di Wajahmu', 'Tere Liye', 'Republika', 2016, 1, 'g210.jpg', 2, 'Novel filosofis ini menggali makna hidup, cinta, dan perjalanan spiritual seorang lelaki yang mencari jawaban atas pertanyaan-pertanyaan terdalam tentang eksistensi. Melalui pengalaman, pertemuan, dan refleksi batin, ia menapaki jalan penuh ujian yang menuntunnya pada pemahaman akan arti kebahagiaan sejati, keikhlasan, serta hubungan manusia dengan Tuhan dan sesamanya. Dengan bahasa puitis dan penuh perenungan, kisah ini menghadirkan perjalanan batin yang menyentuh hati sekaligus mengajak pembaca merenungkan kembali tujuan hidup.'),
(72, 'Rindu', 'Tere Liye', 'Republika', 2014, 1, 'g213.jpg', 2, 'Novel ini mengisahkan perjalanan haji di abad ke-19 yang sarat dengan drama kehidupan, pencarian pengampunan, dan pengalaman spiritual mendalam. Di tengah kerasnya perjalanan panjang menuju Tanah Suci pada masa itu, para tokohnya harus menghadapi ujian fisik, konflik batin, serta pertemuan yang membuka mata tentang makna ikhlas dan kesabaran. Cerita ini bukan hanya menggambarkan tradisi dan sejarah ibadah haji tempo dulu, tetapi juga mengangkat nilai-nilai kemanusiaan, pengorbanan, dan keagungan spiritual yang menjadikan perjalanan tersebut sebuah transformasi hidup.'),
(73, 'Sunset Bersama Rosie', 'Tere Liye', 'Mahaka Publishing', 2014, 1, 'g.jpg', 2, 'Novel romantis yang menyajikan kisah cinta penuh konflik emosional dan refleksi kehidupan.'),
(74, 'Telur Keledai dan Kentut Seorang sufi', 'Usamah Abubakar', 'Navila', 2010, 1, 'g52.jpg', 2, 'Buku ini berisi kumpulan cerita sufistik yang dikemas dengan gaya humor segar dan filosofi sederhana, sehingga mudah dipahami oleh pembaca dari berbagai kalangan. Setiap kisahnya menyajikan nilai-nilai kebijaksanaan sufistik yang mendalam, namun disampaikan dengan cara ringan dan menghibur tanpa kehilangan makna. Melalui tokoh-tokoh yang unik serta dialog penuh kelucuan, pembaca diajak merenungkan hakikat kehidupan, cinta, dan hubungan manusia dengan Sang Pencipta. Buku ini menjadi bacaan inspiratif yang bukan hanya menghibur, tetapi juga memberikan pelajaran spiritual yang menyentuh hati.'),
(75, 'Incognito', 'Windhy Puspitadewi', 'PT Gramedia Pustaka Utama', 2014, 1, 'g194.jpg', 2, 'Novel remaja romantis ini mengisahkan perjalanan cinta yang dibumbui konflik identitas dan rahasia besar yang selama ini tersembunyi. Di balik hubungan manis yang terjalin antara tokoh utama, muncul ketegangan ketika masa lalu dan jati diri yang sebenarnya mulai terungkap, mengguncang kepercayaan sekaligus memperdalam emosi cerita. Dengan sentuhan drama yang penuh kejutan, novel ini menghadirkan dilema antara cinta, kejujuran, dan keberanian untuk menerima kenyataan.'),
(76, 'She', 'Windhy Puspitadewi', 'PT Gramedia Pustaka Utama', 2015, 1, 'g206.jpg', 2, 'Novel ini mengisahkan perjalanan penuh warna tentang persahabatan, cinta, dan pencarian arti keberanian sejati. Para tokohnya menghadapi berbagai ujian hidup yang menuntut mereka untuk saling mendukung, memahami perasaan masing-masing, sekaligus belajar berani mengambil keputusan penting. Dengan alur yang hangat dan emosional, cerita ini menggambarkan bagaimana cinta dan persahabatan mampu menumbuhkan keberanian untuk menghadapi tantangan hidup serta menemukan jati diri.'),
(77, 'Touche', 'Windhy Puspitadewi', 'PT Gramedia Pustaka Utama', 2011, 1, 'g195.jpg', 2, 'Novel fantasi romantis ini memadukan kisah cinta dengan sentuhan magis yang menghubungkan dunia nyata dan kekuatan ajaib. Tokoh utama harus menghadapi pilihan sulit antara menjalani kehidupan biasa atau mengikuti takdirnya yang penuh misteri, sembari menjaga hubungan cinta yang diuji oleh rahasia besar dan kekuatan yang tak terduga. Dengan alur penuh kejutan, novel ini menghadirkan perpaduan antara romansa, petualangan, dan keajaiban yang memikat.'),
(78, 'Misi #22: Tantangan', 'Yunita Candra Sari', 'DAR! Mizan', 2005, 1, 'g30.jpg', 2, 'Novel remaja ini menghadirkan kisah penuh semangat tentang lika-liku persahabatan yang diwarnai berbagai tantangan, keceriaan, sekaligus ujian kedewasaan. Tokoh-tokohnya digambarkan dengan karakter yang kuat dan dinamis, saling mendukung namun juga kerap berselisih karena perbedaan pandangan dan perasaan. Dengan alur yang ringan namun menyentuh, cerita ini mengajak pembaca untuk merasakan manis-pahitnya masa remaja, pentingnya kebersamaan, serta pelajaran berharga bahwa persahabatan sejati adalah tentang kesetiaan, pengertian, dan keberanian menghadapi perubahan hidup bersama-sama.'),
(79, 'Markum Yang Cerdik', 'Zaenal Radar T', 'DAR! Mizan', 2004, 1, 'g.jpg', 2, 'Cerita anak penuh humor tentang Markum yang pintar mencari solusi dalam masalah.'),
(80, 'Chicken Soup for The Soul : Pelajaran Berharga', 'Kim Donghwa', 'PT Gramedia Pustaka Utama', 2006, 1, 'g187.jpg', 3, 'Komik inspiratif ini berisi kisah-kisah sederhana yang sarat makna dan penuh pelajaran hidup, dikemas dengan ilustrasi menarik dan alur yang ringan. Setiap cerita menggambarkan pengalaman sehari-hari yang dekat dengan pembaca, namun mampu menyampaikan pesan moral tentang kejujuran, persahabatan, kerja keras, dan ketulusan hati. Dengan gaya visual yang menyenangkan, komik ini tidak hanya menghibur, tetapi juga memberikan inspirasi bagi pembaca untuk melihat sisi positif dalam hidup. Sebuah bacaan yang cocok untuk semua kalangan, terutama generasi muda yang ingin belajar nilai kehidupan dengan cara yang menyenangkan.'),
(81, '99 Pesan Nabi', 'vbi_djenggotten', 'Zahira', 2014, 1, 'g217.jpg', 3, 'Komik islami ini menyampaikan pesan-pesan moral Nabi dengan cara ringan, sederhana, dan mudah dipahami oleh semua kalangan, khususnya anak-anak dan remaja. Melalui ilustrasi menarik dan cerita singkat, komik ini menghadirkan nilai-nilai kebaikan seperti kejujuran, kesabaran, kasih sayang, serta tolong-menolong dalam keseharian. Disusun dengan gaya yang menghibur namun tetap sarat makna, buku ini menjadi media belajar akhlak islami yang menyenangkan sekaligus mendidik.'),
(82, 'Islam Sehari-hari', 'VBi_djenggotten', 'Qultum Media', 2013, 1, 'g186.jpg', 3, 'Komik edukatif ini menyajikan cerita ringan yang mengajarkan nilai-nilai Islam melalui berbagai aktivitas sehari-hari. Dengan ilustrasi yang menarik dan alur yang sederhana, komik ini mudah dipahami oleh anak-anak maupun remaja, sekaligus menyenangkan untuk dibaca. Setiap kisahnya mengandung pesan moral islami, mulai dari pentingnya kejujuran, disiplin, rasa syukur, hingga kepedulian terhadap sesama. Disampaikan dengan gaya yang lucu dan dekat dengan kehidupan pembaca muda, komik ini tidak hanya menghibur tetapi juga mendidik, menjadikannya sarana belajar yang efektif untuk menanamkan akhlak mulia sejak dini.'),
(83, 'Inikah Balasanmu, Anakku ?', '@haditsku', 'Wahyu Qolbu', NULL, 1, 'g236.jpg', 4, 'Buku ini merupakan buku islami yang berisi nasihat dan pelajaran berharga tentang hubungan antara anak dan orang tua, disajikan dengan bahasa yang lembut dan penuh makna. Melalui ayat-ayat Al-Qur’an, hadis, serta kisah-kisah inspiratif, penulis mengajak pembaca untuk merenungi betapa besar kasih sayang dan pengorbanan orang tua, serta pentingnya berbakti kepada mereka sepanjang hidup. Buku ini juga membahas bagaimana orang tua dapat menjadi teladan dalam mendidik anak dengan penuh cinta, kesabaran, dan nilai-nilai keislaman. Disertai refleksi dan renungan, setiap bab memberikan panduan praktis untuk mempererat ikatan keluarga, menumbuhkan rasa hormat, serta membangun komunikasi yang penuh kasih. Bacaan ini sangat cocok bagi siapa pun yang ingin memperbaiki hubungan keluarga dan meneladani ajaran Islam dalam membina keharmonisan antara anak dan orang tua.'),
(84, 'Daily Dose Of Shine', 'A Fuadi', 'PT Gramedia Pustaka Utama', 2018, 1, 'g40.jpg', 4, 'Buku ini merupakan kumpulan inspirasi harian yang dirancang untuk menyemangati pembaca dalam menjalani hidup dengan penuh optimisme. Setiap halaman berisi kata-kata motivasi, refleksi sederhana, dan pesan penuh makna yang mudah dipahami serta relevan dengan keseharian. Dengan gaya penulisan yang ringan namun menyentuh, buku ini mengajak pembaca untuk melihat sisi positif dari setiap peristiwa, menemukan kekuatan dalam diri, serta menjaga semangat meski menghadapi berbagai tantangan. Bacaan ini menjadi teman setia yang memberikan energi baru setiap hari, membantu menumbuhkan sikap optimis dan percaya diri dalam menapaki perjalanan hidup.'),
(85, 'Kunci Sukses Meraih Cinta Ilahi', 'Aam Amiruddin', 'Khazanah Intelektual', 2009, 1, 'g.jpg', 4, 'Buku motivasi islami yang mengajarkan bagaimana meraih cinta Allah melalui ibadah dan akhlak.'),
(86, 'Diam Sejenak', 'Abdul Mufid', 'Adi Citra Cemerlang', 2015, 1, 'g227.jpg', 2, 'Buku ini merupakan kumpulan refleksi kehidupan yang ditulis dengan bahasa sederhana namun sarat makna, ditujukan untuk membantu pembaca menenangkan jiwa dan memperbaiki diri. Setiap tulisan di dalamnya mengajak pembaca merenungi perjalanan hidup, mengenali kelemahan diri, serta menemukan ketenangan melalui keikhlasan, kesabaran, dan rasa syukur. Disertai kisah-kisah inspiratif dan renungan spiritual, buku ini menyentuh berbagai aspek kehidupan — mulai dari hubungan dengan diri sendiri, sesama manusia, hingga dengan Tuhan. Pesan-pesan moral yang terkandung di dalamnya memberikan dorongan untuk terus berbuat baik, memperbaiki niat, dan menemukan makna sejati dari kebahagiaan yang sederhana. Cocok dibaca saat hati lelah atau membutuhkan motivasi, buku ini menjadi sahabat yang menuntun pembacanya untuk lebih dekat dengan kedamaian batin dan nilai-nilai kehidupan yang hakiki.'),
(87, 'Melangkah Searah', 'Aji Nur Afifah', 'Qultum Media', 2019, 1, 'g69.jpg', 4, 'Buku motivasi islami ini ditujukan untuk generasi muda agar tetap istiqamah dalam iman sekaligus bersemangat menjalani kehidupan. Dengan bahasa yang ringan dan mudah dipahami, buku ini menyajikan pesan-pesan inspiratif tentang pentingnya menjaga hati, konsistensi dalam ibadah, serta keberanian menghadapi tantangan hidup sesuai nilai-nilai Islam. Disertai kisah nyata, refleksi, dan nasihat praktis, buku ini mengajak pembaca muda untuk terus optimis, tidak mudah menyerah, serta menjadikan iman sebagai fondasi dalam meraih cita-cita. Sebuah bacaan yang memberi semangat baru sekaligus menuntun pada jalan kebaikan.'),
(88, 'Kami Berani Beda', 'Akbar Rabbani, dkk', 'Dompet Dhuafa', 2014, 1, 'g230.jpg', 2, 'Buku ini berisi kumpulan kisah inspiratif dari anak-anak muda yang berani mengambil jalan berbeda demi kebaikan dan perubahan positif. Setiap cerita menampilkan perjuangan nyata mereka dalam menghadapi tantangan, menolak arus negatif, serta memilih langkah yang sesuai dengan hati nurani dan nilai-nilai kebaikan. Dengan gaya penceritaan yang hangat dan memotivasi, buku ini menunjukkan bahwa keberanian untuk menjadi “berbeda” bukanlah kelemahan, melainkan kekuatan untuk memberikan dampak nyata bagi diri sendiri dan lingkungan. Kisah-kisah di dalamnya mengajarkan pentingnya integritas, ketulusan, semangat, dan keyakinan pada mimpi, menjadikannya bacaan yang menggugah semangat generasi muda untuk terus berbuat baik, mengejar cita-cita, dan menginspirasi sesama.'),
(89, 'Diary Taubat', 'Alia Yumadiawati', 'Lingkar Pena', 2008, 1, 'g68.jpg', 4, 'Buku islami ini berisi catatan perjalanan spiritual menuju taubat dan kedekatan dengan Allah, ditulis dengan bahasa yang sederhana namun penuh makna. Melalui kisah reflektif, renungan, serta pengalaman yang menyentuh, pembaca diajak memahami arti pentingnya kembali kepada Allah setelah jatuh dalam kesalahan. Setiap halaman menyajikan pesan tentang keikhlasan, harapan, dan kasih sayang Allah yang selalu terbuka bagi hamba-Nya yang ingin bertaubat. Buku ini menjadi pengingat sekaligus motivasi agar setiap langkah hidup senantiasa diarahkan menuju kebaikan dan cinta kepada Sang Pencipta.'),
(90, '4 You, Ladies', 'Aprilia Kartika', 'Republika', 2015, 1, 'g.jpg', 4, 'Buku motivasi islami untuk wanita, membahas cinta, persahabatan, dan iman.'),
(91, 'Jangan Jadi Muslimah Nyebelin', 'Asma Nadia', 'Lingkar Pena', 2005, 1, 'g191.jpg', 4, 'Buku ringan ini ditujukan khusus untuk muslimah, menyajikan nasihat-nasihat praktis yang membantu pembaca agar lebih bijak dalam menghadapi berbagai aspek kehidupan. Dengan gaya bahasa yang sederhana namun menyentuh, penulis membahas tema-tema seputar peran muslimah dalam keluarga, pergaulan, karier, hingga perjalanan spiritual. Setiap nasihat disampaikan dengan penuh kelembutan, sehingga mudah dipahami dan diterapkan dalam keseharian. Buku ini menjadi sahabat inspiratif bagi muslimah yang ingin terus memperbaiki diri, menjaga akhlak, dan menjalani hidup dengan lebih tenang, dewasa, serta penuh makna.'),
(92, 'Pesan-Pesan Nabi Untuk Wanita', 'Badwi Mahmud Al-Syaikh', 'Salam Books', 2015, 1, 'g218.jpg', 4, 'Buku islami ini berisi kumpulan nasihat dan petunjuk Nabi khusus untuk perempuan, yang mencakup berbagai aspek kehidupan mulai dari ibadah, akhlak, peran dalam keluarga, hingga hubungan sosial. Disajikan dengan bahasa sederhana dan penjelasan yang mudah dipahami, buku ini membantu muslimah meneladani ajaran Rasulullah dalam menjalani kehidupan sehari-hari. Dengan memadukan hadis, kisah teladan, dan penafsiran ulama, karya ini menjadi panduan praktis agar perempuan dapat menjaga kehormatan, meningkatkan keimanan, serta meraih kebahagiaan dunia dan akhirat.'),
(93, 'Kamu Nggak Bego, Kok!', 'Bambang Q Anees', 'DAR! Mizan', 2004, 1, 'g39.jpg', 4, 'Buku motivasi ini ditujukan untuk remaja dengan gaya penyampaian yang humoris, segar, dan penuh semangat, sehingga membuat pembaca merasa dekat dan tidak digurui. Melalui kisah-kisah ringan, pengalaman sehari-hari, serta sentuhan humor yang menghibur, buku ini menyemangati pembaca agar lebih percaya diri menghadapi tantangan hidup dan berani mengejar mimpi. Pesan-pesan positif yang dikemas secara sederhana membantu remaja memahami pentingnya bersikap optimis, menghargai diri sendiri, serta tetap berusaha meski sering kali menghadapi kegagalan. Bacaan ini menjadi teman inspiratif yang mampu memotivasi tanpa kehilangan sisi hiburan.'),
(94, 'Ibu Itu Sungguh Ajaib!', 'Bilif Abduh', 'Laksana', 2011, 1, 'g189.jpg', 4, 'Buku ini merupakan kumpulan kisah inspiratif yang menyoroti peran mulia seorang ibu dalam keluarga dan kehidupan. Melalui cerita-cerita yang menyentuh hati, pembaca diajak merasakan pengorbanan, kasih sayang, dan keteguhan hati seorang ibu dalam mendidik anak, menjaga rumah tangga, serta menjadi sumber kekuatan di tengah berbagai ujian hidup. Disampaikan dengan bahasa yang hangat dan penuh emosi, setiap kisah menghadirkan pesan moral tentang cinta tanpa syarat, keikhlasan, dan doa yang tak pernah putus. Buku ini menjadi pengingat indah tentang betapa berharganya sosok ibu dan betapa besar pengaruhnya dalam membentuk masa depan keluarga.');
INSERT INTO `buku` (`id`, `judul`, `pengarang`, `penerbit`, `tahun`, `quantity`, `gambar`, `id_kategori`, `deskripsi`) VALUES
(95, 'Asyiknya Jadi Orang Gajian', 'Dion Yulianto', 'C-Klik Media', 2019, 1, 'g182.jpg', 4, 'Buku ringan ini membahas pengalaman sehari-hari para pekerja bergaji dengan balutan humor segar dan renungan yang mengena. Setiap kisahnya diambil dari realitas dunia kerja, mulai dari rutinitas kantor, drama akhir bulan, hingga lika-liku menghadapi atasan dan rekan kerja. Dengan gaya penulisan yang santai dan jenaka, penulis mampu menghadirkan cerita yang membuat pembaca tertawa sekaligus merenung tentang arti perjuangan mencari nafkah. Buku ini menjadi teman bacaan yang menghibur namun tetap sarat makna, cocok untuk siapa saja yang ingin menemukan sisi lucu di balik kesibukan kerja sehari-hari.'),
(96, 'Jurus Sehat Rasulullah : Hidup Sehat Menebar Manfaat', 'dr. Zaidul Akbar', 'PT Sigma Media Inovasi', 2020, 1, 'g.jpg', 4, 'Panduan kesehatan islami berdasarkan sunnah Nabi untuk hidup lebih sehat dan bermanfaat.'),
(97, '#4 Terampil Belajar. Cara Asyik Biar Pintar', 'Gina Al Ilmi', 'PT Syaamil Cipta Media', 2007, 1, 'g232.jpg', 2, 'Buku ini merupakan panduan praktis untuk meningkatkan keterampilan belajar anak dan remaja, disusun secara ringkas, aplikatif, dan mudah diterapkan dalam kehidupan sehari-hari. Di dalamnya dijelaskan berbagai teknik belajar aktif seperti cara membuat ringkasan yang efektif, mengatur waktu belajar dengan efisien, menerapkan strategi pengulangan sederhana, serta mempertahankan motivasi belajar jangka panjang. Buku ini juga membahas aspek psikologis penting, termasuk cara mengatasi rasa malas, cemas, dan kebosanan yang sering muncul saat belajar. Setiap bab dilengkapi dengan contoh latihan dan aktivitas menarik yang bisa dilakukan di rumah maupun di sekolah, sehingga pembaca dapat langsung mempraktikkan setiap metode yang dipelajari. Dengan pendekatan yang inspiratif dan relevan, buku ini menjadi teman belajar yang ideal untuk membantu anak dan remaja mencapai hasil belajar optimal dan sikap positif terhadap proses pendidikan.'),
(98, 'Jodoh Dunia Akhirat', 'Ikhsanun Kamil, Foezi Citra Cuaca', 'Mizania', 2016, 1, '98.jpg', 2, 'Membahas konsep pernikahan dan jodoh dari sisi agama, sosial, dan psikologis. Menguraikan cara memilih pasangan, memaknai ujian dalam hubungan, serta menyatukan tujuan duniawi dan ukhrawi. Berisi panduan komunikasi, etika taaruf, dan langkah memperbaiki diri sebelum menikah. Ditulis dengan bahasa ringan dan aplikatif.'),
(99, 'Maka Tersenyumlah, Cantik', 'Indari Mastuti', 'Khansa', 2005, 1, 'g237.jpg', 2, 'Buku ini merupakan buku motivasi untuk perempuan muda yang membahas tentang kepercayaan diri, kecantikan batin, dan pengembangan diri dengan pendekatan yang hangat, ringan, dan menyemangati. Melalui cerita-cerita inspiratif, refleksi diri, dan latihan praktis, penulis mengajak pembaca untuk mengenal jati diri, membangun harga diri yang kuat, serta menumbuhkan kebiasaan positif dalam kehidupan sehari-hari. Buku ini juga menyoroti tekanan sosial dan standar kecantikan yang sering membuat perempuan muda merasa kurang berharga, lalu mengajarkan cara menghadapi hal tersebut dengan keyakinan, kebijaksanaan, dan cinta diri. Dengan gaya penulisan yang lembut namun penuh semangat, buku ini menjadi sahabat yang menuntun perempuan untuk memahami bahwa kecantikan sejati bersumber dari hati yang tulus, pikiran yang positif, dan keberanian menjadi diri sendiri.'),
(100, '99 Mutiara Hijabers', 'Kang Maman', 'Muara', 2015, 1, 'g178.jpg', 2, 'Buku ini berisi kumpulan 99 pesan inspiratif tentang kehidupan berhijab yang dirancang sebagai panduan sekaligus motivasi harian bagi muslimah modern. Disajikan dengan bahasa ringan dan penuh makna, setiap pesan memuat nasihat, kisah nyata, serta tips praktis agar hijabers tetap konsisten dalam menjaga syariat, tampil elegan, dan beretika di berbagai situasi. Topik yang dibahas meliputi gaya busana syar’i yang sesuai tuntunan Islam, etika sosial dalam pergaulan, cara merawat iman di tengah godaan zaman, hingga strategi menghadapi stereotip masyarakat. Buku ini menjadi sahabat inspiratif yang menguatkan muslimah untuk senantiasa percaya diri, berkarakter, dan istiqamah dalam berhijab.'),
(101, 'Awas Ada Setan! Taaruf Sama Setan, Maka Tak Takut', 'Kang Yadi', 'Lingkar Pena', 2008, 1, 'g212.jpg', 2, 'Buku dakwah populer ini menggunakan metafora unik “taaruf dengan setan” untuk menjelaskan bagaimana godaan negatif bekerja dalam kehidupan manusia. Penulis mengupas jebakan emosi, kebiasaan buruk, dan kelemahan diri yang sering dimanfaatkan setan untuk menjerumuskan manusia. Disajikan dengan bahasa sederhana, kisah nyata, dan anekdot yang dekat dengan keseharian, buku ini juga memberikan solusi praktis melalui dzikir, pengendalian nafsu, serta pentingnya memilih lingkungan yang baik. Dengan gaya ringan dan penuh renungan, buku ini mengajak pembaca lebih waspada sekaligus optimis dalam memperkuat iman.'),
(102, 'Salam Pancasila Sebagai Salam Kebangsaan', 'Khoirul Anam', 'CAKRAWALA', 0, 1, 'g180.jpg', 2, 'Buku ini mengulas secara komprehensif gagasan Salam Pancasila sebagai bentuk salam kebangsaan yang berperan penting dalam memperkuat identitas nasional dan meneguhkan semangat persatuan. Penulis membahas sejarah lahirnya salam ini, makna filosofis yang terkandung di dalamnya, serta urgensinya dalam memperkokoh persaudaraan di tengah keberagaman bangsa Indonesia. Selain itu, buku ini juga menguraikan nilai-nilai Pancasila beserta aplikasinya dalam kehidupan bermasyarakat, berbangsa, dan bernegara, sehingga pembaca dapat memahami bahwa Salam Pancasila bukan sekadar sapaan simbolis, melainkan refleksi komitmen bersama untuk menjaga keutuhan dan persatuan bangsa.'),
(103, 'Cara-Cara Belajar Ilmuwan-Ilmuwan Muslim Pencetus Sains-Sains Canggih Modern', 'M. Yusuf Abdurrahman', 'Diva Press', 2013, 1, 'g183.jpg', 2, 'Buku ini memperkenalkan metode belajar dan cara berpikir para ilmuwan Muslim klasik yang menjadi peletak dasar bagi perkembangan sains modern. Tokoh-tokoh besar seperti Ibnu Sina, Al-Khawarizmi, Al-Farabi, hingga Ibnu Haitsam dipaparkan kontribusinya dalam berbagai bidang ilmu, mulai dari kedokteran, matematika, filsafat, hingga astronomi. Dengan bahasa populer dan mudah dipahami, penulis tidak hanya menyajikan kisah intelektual mereka, tetapi juga menekankan semangat belajar, penelitian, dan dedikasi yang dapat menginspirasi generasi masa kini. Buku ini menjadi bacaan penting bagi siapa saja yang ingin memahami akar kejayaan peradaban Islam serta menggali nilai-nilai yang relevan untuk membangun tradisi ilmu di era modern.'),
(104, 'Aku Melihat Surga di Dunia', 'Ma\'riifatullah', 'Gen Mirqat', 2008, 1, 'g190.jpg', 2, 'Buku motivasi spiritual ini menggambarkan bagaimana menemukan ketenangan batin, keindahan iman, dan kebahagiaan sejati di tengah hiruk-pikuk kehidupan dunia. Melalui kisah-kisah inspiratif dan refleksi mendalam, penulis menghadirkan perjalanan ruhani yang menuntun pembaca untuk lebih dekat kepada Allah, memahami makna hidup, serta merasakan kebahagiaan hakiki yang bisa dicapai sejak di dunia. Ditulis dengan bahasa yang lembut dan menyejukkan hati, buku ini menjadi panduan bagi siapa saja yang merindukan kedamaian jiwa, sekaligus pengingat bahwa iman dan penyerahan diri kepada Allah adalah kunci utama kebahagiaan sejati.'),
(105, 'Now, You Are Leader!', 'Muhammad Fathi', 'Bening Publishing', 2004, 1, 'g229.jpg', 2, 'Buku ini merupakan panduan inspiratif tentang kepemimpinan bagi generasi muda, ditulis dengan gaya bahasa yang sederhana dan membumi agar mudah dipahami oleh pembaca dari berbagai kalangan. Melalui penjelasan yang runtut dan penuh motivasi, penulis mengajak pembaca untuk menemukan potensi kepemimpinan dalam diri sendiri, belajar mengambil keputusan yang bijak, membangun visi hidup yang jelas, serta memahami pentingnya memimpin diri sendiri sebelum memimpin orang lain. Setiap bab disertai contoh nyata dan kisah inspiratif yang menggugah semangat untuk terus berkembang menjadi pribadi yang tangguh, visioner, dan berintegritas. Buku ini sangat cocok bagi pelajar, mahasiswa, maupun siapa pun yang ingin menumbuhkan jiwa kepemimpinan dan menjadi pemimpin masa depan yang berkarakter, berempati, dan membawa perubahan positif.'),
(106, 'Buku Pintar Mitologi Dunia', 'Nurla Isna Aunillah', 'FlashBooks', 2011, 1, '106.jpg', 2, 'Buku ini menyajikan kisah-kisah mitologi dari berbagai peradaban dunia, mulai dari Yunani, Romawi, Mesir, hingga Asia. Disusun secara ringkas dan menarik, pembaca diajak memahami cerita dewa, pahlawan, dan legenda yang membentuk kebudayaan manusia. Sangat cocok sebagai bahan bacaan ringan maupun referensi pengetahuan sejarah dan budaya.'),
(107, 'Metodologi Penelitian Ekonomi & Sosial : Teori, Konsep, dan Rencana Proposal', 'Nurlina T Muhyiddin et al', 'Salemba Empat', 2018, 1, '107.jpg', 2, 'Sebuah buku akademik yang komprehensif mengenai metodologi penelitian di bidang ekonomi dan sosial. Menyajikan teori, konsep, serta panduan penyusunan proposal penelitian. Cocok untuk mahasiswa, dosen, maupun peneliti yang ingin memahami metodologi penelitian dengan pendekatan sistematis dan praktis.'),
(108, 'Bawa Perubahan', 'Rheinhald Kasali', 'Noura', 2017, 1, '108.jpg', 2, 'Ditulis oleh pakar manajemen dan perubahan, buku ini mengajak pembaca untuk berani keluar dari zona nyaman dan menjadi agen perubahan. Disertai kisah nyata dan refleksi pribadi, buku ini memberikan strategi bagaimana menghadapi tantangan hidup, memimpin perubahan, dan menumbuhkan mentalitas inovatif.'),
(109, 'Rich Dad Poor Dad', 'Robert T Kiyosaki', 'PT Gramedia Pustaka Utama', 2001, 1, 'g240.jpg', 2, 'Buku ini merupakan buku finansial populer internasional karya Robert T. Kiyosaki berjudul Rich Dad Poor Dad, yang telah menjadi klasik dalam dunia literasi keuangan. Melalui kisah nyata antara dua sosok ayah — “Rich Dad” (ayah kaya) dan “Poor Dad” (ayah miskin) — Kiyosaki menggambarkan perbedaan cara berpikir tentang uang, pekerjaan, dan investasi antara orang kaya dan orang miskin. Buku ini memperkenalkan konsep penting tentang aset dan liabilitas, menekankan bahwa kebebasan finansial hanya dapat dicapai dengan memiliki aset produktif yang menghasilkan pendapatan pasif. Selain itu, Kiyosaki juga menyoroti pentingnya pendidikan finansial, keberanian mengambil risiko, dan kebiasaan berpikir mandiri dalam mengelola keuangan. Dengan gaya penulisan yang mudah dipahami dan penuh inspirasi, buku ini menjadi bacaan wajib bagi siapa pun yang ingin membangun pola pikir kaya dan mencapai kebebasan finansial sejati.'),
(110, 'The Cashflow Quadrant', 'Robert T Kiyosaki', 'PT Gramedia Pustaka Utama', 2001, 1, 'g239.jpg', 1, 'Buku ini merupakan lanjutan dari Rich Dad Poor Dad karya Robert T. Kiyosaki, yang memperkenalkan konsep terkenal bernama Cashflow Quadrant — empat tipe cara orang menghasilkan uang: Employee (E), Self-Employed (S), Business Owner (B), dan Investor (I). Melalui penjelasan yang jelas dan penuh contoh nyata, Kiyosaki menguraikan perbedaan pola pikir antara orang yang berada di kuadran kiri (E dan S) dengan mereka yang berada di kuadran kanan (B dan I), serta bagaimana berpindah dari mencari keamanan finansial menuju kebebasan finansial sejati. Buku ini juga menyajikan strategi praktis dalam membangun bisnis, mengelola aset, dan berinvestasi secara cerdas, disertai pengalaman pribadi penulis yang menggugah. Dengan gaya penyampaian yang sederhana namun penuh wawasan, buku ini menjadi panduan penting bagi siapa pun yang ingin mengubah cara berpikir tentang uang, pekerjaan, dan kebebasan finansial.'),
(111, 'Laki-Laki yang Tak Berhenti Menangis : Kumpulan Kisah Islami Penyejuk Hati', 'Rusdi Mathari', 'Buku Modjok', 2019, 1, 'g221.jpg', 2, 'Buku ini merupakan kumpulan kisah Islami yang menyentuh hati, penuh makna, dan sarat dengan pesan moral. Setiap cerita di dalamnya menghadirkan inspirasi tentang keikhlasan, kesabaran, serta keteguhan iman dalam menghadapi berbagai ujian hidup. Dengan gaya bahasa yang sederhana dan mengalir, kisah-kisah tersebut mampu menjadi pengingat yang menyejukkan jiwa sekaligus memberikan motivasi untuk terus berpegang teguh pada nilai-nilai Islam dalam kehidupan sehari-hari.'),
(112, 'La Tahzan for Muslimah', 'Salma Shulha', 'DAR! Mizan', 2007, 1, '112.jpg', 2, 'Buku motivasi Islami khusus untuk muslimah yang terinspirasi dari karya terkenal La Tahzan. Memberikan semangat, penghiburan, dan panduan agar wanita muslimah tetap tegar, sabar, dan optimis dalam menghadapi masalah hidup. Disampaikan dengan gaya bahasa lembut, menyentuh, dan menguatkan iman.'),
(113, '99 Notes For Muslimah', 'Satria Nova', 'Mizania', 2017, 1, '113.jpg', 2, 'Buku ini berisi 99 catatan inspiratif khusus muslimah. Disajikan dalam bentuk motivasi, renungan, serta tips praktis agar muslimah tetap istiqomah, berprestasi, dan percaya diri. Dilengkapi kutipan dan kisah nyata yang menguatkan iman dan kepribadian.'),
(114, 'Jangan Kuliah! Kalau Gak Sukses', 'Setia Furqon Kholid', 'Rumah Karya', 2012, 1, 'g175.jpg', 2, 'Buku motivasi ini ditujukan bagi mahasiswa agar kuliah tidak sekadar menjadi formalitas, melainkan jalan untuk meraih kesuksesan akademik dan menyiapkan masa depan yang gemilang. Penulis menekankan pentingnya memiliki tujuan yang jelas, strategi belajar yang efektif, serta kemampuan membangun jejaring yang bermanfaat. Disampaikan dengan bahasa yang lugas dan inspiratif, buku ini memberikan panduan praktis tentang bagaimana mengoptimalkan waktu kuliah, mengasah keterampilan, serta mengaitkan teori dengan praktik di dunia kerja. Bacaan ini sangat cocok bagi mahasiswa yang ingin menjalani perkuliahan dengan penuh makna, berprestasi, sekaligus mempersiapkan diri menghadapi tantangan karier setelah lulus.'),
(115, 'Aisyah Kekasih yang Terindah', 'Sulaiman An-Nadawi', 'Republika', 2017, 1, 'g216.jpg', 2, 'Buku ini mengisahkan kehidupan Aisyah ra., istri Nabi Muhammad SAW, dengan pendekatan sejarah yang dipadukan kisah inspiratif. Diceritakan bagaimana kecerdasan, kelembutan, dan keteguhan beliau menjadikan Aisyah salah satu figur perempuan utama dalam Islam yang penuh keteladanan. Dari perannya dalam rumah tangga Rasulullah hingga kontribusinya dalam menyebarkan ilmu dan hadis, buku ini menghadirkan potret pribadi Aisyah yang cerdas, tegas, dan penuh kasih sayang. Dengan narasi yang menyentuh, kisah ini menjadi sumber inspirasi bagi pembaca untuk meneladani akhlak mulia dan semangat beliau dalam kehidupan sehari-hari.'),
(116, 'Remaja Bau Sorga. Hang Out Bareng Orang Saleh', 'Teguh Iman Perdana', 'Lingkar Pena', 2008, 1, '116.jpg', 2, 'Ditujukan bagi remaja muslim, buku ini mengajak anak muda untuk memilih lingkungan pergaulan yang positif dan religius. Dengan gaya bahasa gaul, penulis memberi motivasi agar remaja semakin dekat dengan Allah dan jauh dari pengaruh buruk.'),
(117, '#About Love', 'Tere Liye', 'PT Gramedia Pustaka Utama', 2015, 1, 'g176.jpg', 2, 'Novel populer karya Tere Liye ini membahas berbagai sisi cinta, baik yang manis maupun pahit, melalui rangkaian kisah yang dekat dengan kehidupan sehari-hari pembaca muda. Dengan narasi khasnya yang sederhana namun menyentuh, penulis menghadirkan cerita tentang perasaan, pengorbanan, kebahagiaan, hingga luka yang menyertai perjalanan cinta. Setiap bab mengandung pesan moral yang dalam, mengingatkan pembaca bahwa cinta bukan sekadar rasa memiliki, melainkan juga tentang keikhlasan, kesabaran, dan keberanian menghadapi kenyataan. Sebuah novel yang menghibur sekaligus memberi renungan mendalam tentang arti cinta dalam kehidupan.'),
(118, 'Izinkan Aku Bersujud', 'Tyas Effendi', 'Mizania', 2009, 1, 'g67.jpg', 2, 'Novel islami ini mengisahkan perjalanan spiritual yang dipadukan dengan kisah cinta dan berbagai ujian kehidupan, menghadirkan cerita yang menyentuh dan sarat makna. Melalui tokoh utama yang dihadapkan pada masalah hidup, kegagalan, serta godaan duniawi, penulis menggambarkan proses penyerahan diri sepenuhnya kepada Allah sebagai jalan menemukan ketenangan dan kebahagiaan sejati. Dengan alur yang mengalir indah dan penuh emosi, novel ini tidak hanya menghadirkan romansa islami yang hangat, tetapi juga memberikan pelajaran berharga tentang kesabaran, keikhlasan, dan kekuatan iman dalam menghadapi setiap ujian hidup.'),
(119, 'Menjadi Istri yang Layak Dicintai', 'Uken Junaedi, Deny Riana', 'Matabuku Publishing', 2005, 1, '119.jpg', 2, 'Buku panduan untuk para istri agar mampu menjadi pasangan yang dicintai suami. Membahas komunikasi, kasih sayang, dan nilai agama dalam membangun keluarga harmonis.'),
(120, 'Etika Gaul Islam', 'Zenan Asharfillah', 'Zikrul Remaja', 2005, 1, 'g36.jpg', 2, 'Dengan gaya bahasa yang ringan dan mudah dipahami, buku ini membahas etika pergaulan anak muda menurut ajaran Islam, sehingga tetap relevan dengan kehidupan remaja masa kini. Disertai contoh-contoh nyata dalam keseharian, pembahasan di dalamnya memberikan panduan praktis agar remaja bisa tetap tampil gaul, modern, dan percaya diri tanpa harus meninggalkan nilai-nilai Islami. Buku ini tidak hanya menekankan pentingnya menjaga sikap dan perilaku, tetapi juga mengajarkan bagaimana membangun pergaulan sehat yang membawa manfaat, memperkuat persahabatan, serta menuntun pada jalan kebaikan.'),
(121, 'Ibadah Yuk!', 'Zizi Hefni', 'Saufa', 2014, 1, 'g192.jpg', 2, 'Buku motivasi praktis ini ditujukan bagi anak muda agar semakin bersemangat dalam beribadah dan mendekatkan diri kepada Allah. Disajikan dengan bahasa ringan, komunikatif, dan mudah dipahami, buku ini dilengkapi ilustrasi menarik yang membuat pesan-pesan spiritual terasa lebih hidup dan tidak membosankan. Setiap bab berisi dorongan positif, tips sederhana, serta kisah inspiratif yang mampu membangkitkan motivasi pembaca untuk lebih rajin melaksanakan ibadah sehari-hari. Dengan pendekatan yang segar, buku ini cocok menjadi teman belajar dan penyemangat generasi muda untuk tetap istiqamah di jalan kebaikan.'),
(122, 'Akuntansi Koperasi', 'Adenk Sudarwanto', 'Graha Ilmu', 2013, 1, '122.jpg', 1, 'Buku teks akuntansi yang membahas teori dan praktik akuntansi koperasi. Ditulis untuk mahasiswa dan praktisi yang ingin memahami sistem akuntansi dalam organisasi koperasi.'),
(123, 'Audit dan Jasa Assurance. Pendekatan Terintegrasi. Jilid 1 Ed 12', 'Alvin A Arens et al', 'Erlangga', 2006, 1, 'g147.jpg', 1, 'Buku teks auditing terkemuka ini menjelaskan konsep dasar audit dan jasa assurance dengan pendekatan terintegrasi yang komprehensif. Materinya mencakup prinsip-prinsip audit, peran auditor dalam memastikan keandalan laporan keuangan, serta berbagai standar dan prosedur yang berlaku secara internasional. Disusun oleh pakar di bidangnya, buku ini banyak digunakan di perguruan tinggi sebagai rujukan utama dalam mata kuliah auditing karena penyajiannya yang sistematis, jelas, dan dilengkapi studi kasus nyata. Dengan demikian, buku ini tidak hanya memperkuat pemahaman teoretis, tetapi juga membekali mahasiswa dengan keterampilan praktis yang dibutuhkan dalam dunia profesi akuntansi dan audit.'),
(124, 'Auditing dan Pelayanan Verifikasi Pendekatan Terpadu Jilid 1 Ed 9', 'Alvin A Arens et al', 'Indeks', 2001, 1, 'g146.jpg', 1, 'Buku auditing klasik ini mengupas secara mendalam metode verifikasi dan teknik audit dengan pendekatan terpadu, menjadikannya referensi penting bagi mahasiswa akuntansi maupun auditor pemula. Disajikan dengan penjelasan yang sistematis, buku ini membahas prinsip-prinsip dasar auditing, prosedur pemeriksaan laporan keuangan, serta penerapan teknik verifikasi untuk memastikan keandalan informasi akuntansi. Dengan gaya penyajian yang jelas dan didukung contoh-contoh praktis, buku ini membantu pembaca memahami konsep audit secara menyeluruh sekaligus mempersiapkan diri menghadapi praktik audit di dunia nyata.'),
(125, 'Mahir Komputerisasi Akuntansi dengan MYOB Accounting V.23', 'Arvian Triantoro', 'Andi', 2018, 1, 'g107.jpg', 1, 'Buku ini merupakan panduan lengkap penggunaan MYOB Accounting versi terbaru, yang dirancang khusus untuk membantu pelajar, mahasiswa, maupun praktisi akuntansi memahami cara kerja salah satu software akuntansi paling populer di Indonesia. Disajikan dengan bahasa yang sederhana dan sistematis, buku ini menjelaskan langkah demi langkah mulai dari pengenalan fitur, input transaksi, hingga pembuatan laporan keuangan secara profesional. Dilengkapi contoh kasus nyata, ilustrasi, serta tips praktis, buku ini memudahkan pembaca untuk menguasai penggunaan MYOB, sehingga dapat meningkatkan efisiensi kerja dan ketelitian dalam pengelolaan keuangan.'),
(126, 'Akuntansi Biaya Jilid 1 Ed 12', 'Charles T Horngren et al', 'Erlangga', 2006, 1, '126.jpg', 1, 'Buku teks akuntansi biaya yang membahas konsep dasar, perhitungan biaya, serta penerapannya dalam pengambilan keputusan manajerial. Banyak dijadikan referensi di perguruan tinggi.'),
(127, 'Vocational Training and Assessment', 'Dan Hill et al', 'McGraw Hill - Australia', 0, 1, 'g99.jpg', 1, 'Buku referensi internasional ini membahas secara komprehensif mengenai pelatihan vokasional dan penilaian kompetensi, menjadi panduan penting bagi pendidik, pelatih, maupun praktisi dunia kerja. Di dalamnya dijelaskan standar kompetensi yang berlaku secara global, berbagai teknik asesmen yang efektif, serta strategi pengembangan keterampilan agar sesuai dengan kebutuhan industri modern. Dengan pendekatan praktis dan studi kasus nyata, buku ini membantu pembaca memahami bagaimana merancang program pelatihan yang relevan, menilai hasil belajar secara objektif, dan mempersiapkan tenaga kerja yang kompeten serta siap menghadapi persaingan di era globalisasi.'),
(128, 'Penulisan Ilmiah Implementasi Pada Ilmu Akuntansi', 'Dr. Arie Pratama', 'Kencana', 2022, 1, '128g.jpg', 1, 'Panduan penulisan ilmiah khusus untuk aplikasi pada ilmu akuntansi; membahas metodologi, struktur karya ilmiah, dan studi kasus akuntansi.'),
(129, 'Teori Akunting Buku 2', 'Eldon S. Hendriksen, Michael F. Van Breda', 'Interaksara', NULL, 1, 'g228.jpg', 1, 'Buku ini merupakan teori akuntansi lanjutan yang membahas secara mendalam prinsip-prinsip dasar, konsep konseptual, serta perkembangan modern dalam teori akuntansi. Di dalamnya dijelaskan bagaimana teori-teori akuntansi terbentuk, dikembangkan, dan diterapkan dalam praktik pelaporan keuangan di era globalisasi. Pembahasan meliputi kerangka konseptual akuntansi, pengakuan dan pengukuran aset, liabilitas, ekuitas, hingga isu-isu kontemporer seperti pelaporan berbasis nilai wajar dan standar internasional (IFRS). Buku ini juga dilengkapi dengan studi kasus, contoh penerapan teori, serta analisis kritis terhadap kebijakan akuntansi, menjadikannya sumber belajar yang kaya dan aplikatif. Ditulis dengan gaya akademis namun mudah dipahami, buku ini sangat cocok untuk mahasiswa akuntansi tingkat lanjut, dosen, dan praktisi keuangan yang ingin memperdalam pemahaman tentang landasan teoritis dan evolusi pemikiran dalam dunia akuntansi modern.'),
(130, 'Akuntansi Lanjutan Jilid 1. Ed 9', 'Floyd A Beams et al', 'Erlangga', 2006, 1, '130g.jpg', 1, 'Buku akuntansi lanjutan edisi 9; pembahasan mendalam tentang standar pelaporan dan topik akuntansi tingkat lanjut.'),
(131, 'Akuntansi Keuangan Lanjutan Ed1', 'Hadori Yunus, Harnanto', 'BPFE - Yogyakarta', 2009, 1, '131g.jpg', 1, 'Fokus pada pelaporan keuangan lanjutan dan penerapan standar akuntansi di perusahaan; disertai contoh dan latihan.'),
(132, 'Akuntansi Manajerial Buku 1 Ed 8', 'Hansen, Mowen', 'Salemba Empat', 2012, 1, '132g.jpg', 1, 'Dasar-dasar akuntansi manajerial: perencanaan, pengendalian, dan pengambilan keputusan berbasis informasi biaya.'),
(133, 'Tips Praktis Membangun dan Mengolah Administrasi Sekolah', 'Jamal Ma\'mur Asmani', 'Diva Press', 2011, 1, '133g.jpg', 1, 'Panduan praktis untuk menyusun dan mengelola administrasi sekolah secara efisien, berisi template dan contoh penerapan.'),
(134, 'Akuntansi Intermediate Vol Komprehensif Jilid 1 Ed 9', 'Jay M. Smith, K. Fred Skousen', 'Erlangga', 1996, 1, '134g.jpg', 1, 'Buku intermediate akuntansi komprehensif; cocok untuk memperdalam pemahaman transaksi dan pelaporan menengah hingga lanjutan.'),
(135, 'Research Design : Pendekatan Metode Kualitatif, Kuantitatif, dan Campuran Ed.4', 'John W. Creswell', 'Pustaka Pelajar', 2021, 1, '135g.jpg', 1, 'Referensi metodologi penelitian (kualitatif, kuantitatif, dan mixed methods) lengkap dengan contoh desain penelitian.'),
(136, 'Buku Pintar TOEFL Untuk Pemula', 'Joko S Kahhar', 'Cemerlang Publishing', 2014, 1, 'g224.jpg', 1, 'Buku ini merupakan panduan lengkap untuk persiapan TOEFL bagi pemula, dirancang agar pembaca memahami format ujian, strategi menjawab setiap bagian, dan cara efektif meningkatkan skor. Di dalamnya terdapat penjelasan mendetail mengenai tiga komponen utama TOEFL — Listening, Structure and Written Expression, dan Reading Comprehension — disertai contoh soal, latihan bertahap, serta pembahasan kunci jawaban. Buku ini juga memberikan berbagai tips praktis untuk mengelola waktu saat ujian, memperluas kosa kata, serta meningkatkan kemampuan memahami teks akademik berbahasa Inggris. Dengan pendekatan yang sistematis dan mudah diikuti, buku ini sangat cocok bagi pelajar, mahasiswa, maupun profesional yang baru memulai perjalanan mereka dalam menghadapi TOEFL dan ingin meraih skor tinggi secara percaya diri.'),
(137, 'Analisis Laporan Keuangan Buku 1 Ed 11', 'K.R Subramanyam', 'Salemba Empat', 2017, 1, '137g.jpg', 1, 'Buku analisis laporan keuangan edisi ke-11 jilid 1; membahas teknik dan metode analisis laporan keuangan secara komprehensif.'),
(138, 'Analisis Laporan Keuangan Buku 2 Ed 11', 'K.R Subramanyam', 'Salemba Empat', 2017, 1, 'g226.jpg', 1, 'Buku ini merupakan lanjutan dari jilid pertama edisi ke-11 yang berfokus pada pendalaman metode analisis keuangan melalui berbagai studi kasus dan contoh penerapan nyata di dunia bisnis. Isi buku memperluas konsep dasar analisis laporan keuangan dengan menambahkan pendekatan praktis dalam menilai kinerja perusahaan, perencanaan keuangan, serta pengambilan keputusan investasi. Setiap bab dilengkapi dengan kasus riil perusahaan, analisis data keuangan yang kompleks, dan latihan interpretasi rasio untuk membantu pembaca memahami penerapan teori dalam konteks profesional. Selain itu, buku ini juga membahas tren terbaru dalam pelaporan keuangan, perubahan standar akuntansi, serta teknik modern dalam analisis data keuangan. Ditulis dengan gaya yang sistematis dan aplikatif, buku ini menjadi sumber belajar penting bagi mahasiswa, praktisi keuangan, serta analis yang ingin memperdalam wawasan dan keterampilan mereka dalam analisis keuangan tingkat lanjut.'),
(139, 'Muslim Yang Diperdebatkan', 'Kalis Mardiasih', 'Buku Modjok', 2019, 1, '139g.jpg', 1, 'Buku pemikiran kritis mengenai isu-isu kontemporer dalam masyarakat muslim Indonesia dengan perspektif progresif.'),
(140, 'Analisis Laporan Keuangan', 'Kasmir,S.E.,M.M.', 'Rajawali Pers', 2018, 1, 'g225.jpg', 1, 'Buku ini merupakan referensi komprehensif dalam analisis laporan keuangan yang membahas berbagai rasio keuangan, teknik interpretasi data, serta penerapannya dalam pengambilan keputusan bisnis dan investasi. Di dalamnya dijelaskan cara membaca dan memahami laporan keuangan — mulai dari neraca, laporan laba rugi, hingga arus kas — serta bagaimana menghubungkan setiap komponen untuk menilai kinerja dan kesehatan perusahaan. Pembaca akan mempelajari rasio likuiditas, solvabilitas, profitabilitas, dan aktivitas, lengkap dengan contoh perhitungan dan studi kasus nyata. Selain itu, buku ini mengajarkan cara menggunakan hasil analisis untuk menentukan strategi manajemen, kebijakan investasi, maupun keputusan kredit. Dengan gaya penyajian yang sistematis dan mudah dipahami, buku ini menjadi rujukan populer bagi mahasiswa ekonomi, analis keuangan, dan pelaku bisnis yang ingin menguasai keterampilan analisis laporan keuangan secara mendalam.'),
(141, 'Sawyer\'s Internal Auditing Buku 1. Ed 5', 'Lawrence B Sawyer, et al', 'Salemba Empat', 2003, 1, '141g.jpg', 1, 'Buku auditing internal klasik edisi ke-5; membahas standar, prosedur, dan praktik terbaik dalam internal auditing modern.'),
(142, 'TOEFL Preparation Guide', 'Michael A Pyle', 'WILEY', 2005, 1, '142g.jpg', 1, 'Panduan persiapan TOEFL yang komprehensif; mencakup strategi ujian, latihan soal, dan tips meningkatkan skor.'),
(143, 'Managerial Accounting : The Cornerstone of Business Desicion Making Ed 7', 'Mowen, Hansen, Heitger', 'Cengage Learning', NULL, 1, '143g.jpg', 1, 'Buku akuntansi manajerial edisi ke-7; membahas peran akuntansi dalam pengambilan keputusan bisnis dan studi kasus nyata.'),
(144, 'Sistem Akuntansi', 'Mulyadi', 'Salemba Empat', 2010, 1, '144g.jpg', 1, 'Referensi utama sistem akuntansi di Indonesia; menjelaskan prosedur, dokumen, dan penerapan sistem akuntansi modern.'),
(145, 'Manajemen Strategik', 'Popy Rufaidah', 'Humaniora', 2014, 1, '145g.jpg', 1, 'Buku manajemen strategis yang membahas perencanaan, implementasi, dan evaluasi strategi dalam organisasi.'),
(146, 'Teknik Penulisan Karya Tulis Ilmiah', 'Prof. Dr. Deni Darmawan', 'Rosda', 2019, 1, '146g.jpg', 1, 'Panduan lengkap dalam menulis karya tulis ilmiah, mulai dari metodologi, teknik penulisan, hingga publikasi.'),
(147, 'Psikologi Kependidikan. Perangkat Sistem Pengajaran Modul', 'Prof. Dr. H. Abin Syamsuddin Makmun', 'Rosda', 2012, 1, '147g.jpg', 1, 'Buku psikologi pendidikan yang mengulas teori belajar, psikologi siswa, dan penerapannya dalam sistem pengajaran modul.'),
(148, 'Aplikasi Analisis Multivariate dengan Program IBM SPSS 21 Update PLS Regresi. Ed 7', 'Prof. Dr. H. Imam Ghozali', 'Badan Penerbit Universitas Diponegoro', 2013, 1, '148g.jpg', 1, 'Panduan analisis multivariat menggunakan SPSS 21 dengan update PLS Regresi; berisi teori dan aplikasi praktis.'),
(149, 'Metode Penelitian Kuantitatif, Kualitatif, dan R&D', 'Prof. Dr. Sugiyono', 'Alfabeta', 2022, 1, '149g.jpg', 1, 'Buku metodologi penelitian populer; membahas pendekatan kuantitatif, kualitatif, dan penelitian pengembangan (R&D).'),
(150, 'Statistika Untun Penelitian', 'Prof. Dr. Sugiyono', 'Alfabeta', 2011, 1, '150g.jpg', 1, 'Referensi statistik praktis untuk penelitian; memuat teori dasar, teknik analisis, serta aplikasi dalam berbagai bidang.'),
(151, 'Dasar-Dasar Evaluasi Pendidikan Ed 2', 'Prof. Dr. Suharsimi Arikunto', 'Bumi Aksara', 2015, 1, '151g.jpg', 1, 'Buku klasik tentang evaluasi pendidikan edisi ke-2; membahas konsep, teknik, dan instrumen evaluasi dalam pendidikan.'),
(152, 'Management Control System Ed 11', 'Robert N Anthony, Vijay', 'Salemba Empat', 2009, 1, '152g.jpg', 1, 'Referensi sistem pengendalian manajemen edisi ke-11; mengulas konsep perencanaan, evaluasi, dan strategi organisasi.'),
(153, 'Auditing : Petunjuk Praktis Pemeriksaan Akuntan oleh Akuntan Publik Ed 4', 'Sukrisno Agoes', 'Salemba Empat', 2012, 1, '153g.jpg', 1, 'Panduan auditing edisi ke-4; membahas praktik pemeriksaan akuntan publik lengkap dengan standar profesional.'),
(154, 'Ensiklopedia Sains Seri Pustaka Vol.1 : Cara Hidup Hewan', 'Tim Penulis', 'Pakar Raya', 2005, 1, '157g.jpg', 1, 'Ensiklopedia sains seri pustaka volume 1 yang membahas cara hidup berbagai jenis hewan secara ilmiah.'),
(155, 'Akuntansi Pengantar', 'Suwardjono', 'BPFE - Yogyakarta', 2013, 1, '155g.jpg', 1, 'Buku pengantar akuntansi; membahas dasar-dasar pencatatan, laporan keuangan, dan siklus akuntansi.'),
(156, 'Akuntansi Perbankan : Transaksi Dalam Valuta Asing Ed 3', 'Taswan', 'UPP STIM YKPN', 2008, 1, '156g.jpg', 1, 'Buku akuntansi perbankan edisi ke-3; fokus pada transaksi dalam valuta asing serta pencatatan dan pelaporannya.'),
(157, 'Ensiklopedia Sains Seri Pustaka Vol.2 : Hewan Liar', 'Tim Penulis', 'Pakar Raya', 2005, 1, '158g.jpg', 1, 'Ensiklopedia sains seri pustaka volume 2 dengan fokus pada hewan liar, habitat, dan perilakunya.'),
(158, 'Ensiklopedia Sains Seri Pustaka Vol.3 : Menemukan Sains', 'Tim Penulis', 'Pakar Raya', 2005, 1, 'g243.jpg', 1, 'Buku ini merupakan Ensiklopedia Sains Seri Pustaka Volume 3 yang dirancang untuk memperkenalkan konsep-konsep dasar sains dengan cara yang sederhana, menarik, dan mudah dipahami oleh pembaca segala usia. Melalui penjelasan yang disertai ilustrasi berwarna, eksperimen sederhana, dan contoh-contoh yang dekat dengan kehidupan sehari-hari, buku ini membantu pembaca memahami prinsip-prinsip ilmiah di bidang fisika, kimia, biologi, dan bumi antariksa. Setiap topik disusun secara sistematis agar pembaca dapat belajar sambil berimajinasi dan bereksperimen. Dengan gaya penyajian yang interaktif dan informatif, buku ini menjadi sumber belajar yang menyenangkan sekaligus memperkuat rasa ingin tahu terhadap dunia sains, sangat cocok bagi pelajar, guru, maupun pembaca umum yang ingin mengenal dasar-dasar ilmu pengetahuan dengan cara yang inspiratif.'),
(159, 'Pembelajaran Unggul Bahasa Indonesia SMK/MAK Kelas XII Jilid A', 'Titik Harsiati, Astria Prameswari, Endah Tri Priyatni', 'Bumi Aksara', 2025, 20, '167g.jpg', 1, 'Buku pelajaran unggul bahasa Indonesia SMK/MAK kelas XII jilid A; fokus pada keterampilan analisis teks, menulis, dan apresiasi karya sastra.'),
(160, 'Ensiklopedia Sains Seri Pustaka Vol.6 : Tumbuhan', 'Tim Penulis', 'Pakar Raya', 2005, 1, 'g242.jpg', 1, 'Buku ini merupakan Ensiklopedia Sains Seri Pustaka Volume 6 yang secara khusus membahas berbagai jenis tumbuhan, struktur, fungsi, serta peranannya dalam kehidupan manusia dan lingkungan. Disusun dengan gaya ilmiah namun mudah dipahami, buku ini menjelaskan mulai dari morfologi dan anatomi tumbuhan, proses fotosintesis, klasifikasi, hingga adaptasi tumbuhan terhadap lingkungan. Selain itu, pembaca juga akan menemukan informasi menarik tentang manfaat tumbuhan bagi ekosistem, sumber pangan, obat-obatan, dan keseimbangan alam. Dilengkapi dengan ilustrasi berwarna, foto-foto nyata, dan penjelasan rinci, buku ini menjadi referensi edukatif yang ideal bagi pelajar, guru, maupun pecinta sains yang ingin memperluas pengetahuan tentang dunia tumbuhan secara komprehensif dan menarik.'),
(161, 'Teknik Sepeda Motor SMK/MAK Kelas XI', 'Sutrisno', 'Bumi Aksara', 2025, 20, '161g.jpg', 1, 'Buku ajar SMK/MAK kelas XI yang membahas teknik sepeda motor, perawatan, serta sistem mesin modern.'),
(162, 'Dasar-Dasar Otomotif SMK/MAK Kelas X Jilid 2', 'Sutrisno', 'Bumi Aksara', 2025, 20, '162g.jpg', 1, 'Materi dasar otomotif jilid 2 untuk SMK/MAK kelas X, mencakup sistem kendaraan dan teknologi otomotif.'),
(163, 'Dasar-Dasar Teknik Mesin SMK/MAK Kelas X', 'Dody Kurniawan', 'Bumi Aksara', 2025, 20, 'g252.jpg', 1, 'Buku ini merupakan Buku Ajar Teknik Mesin Dasar untuk SMK/MAK kelas X yang membahas secara komprehensif prinsip-prinsip mekanika, permesinan, dan teknik dasar dalam bidang teknik mesin. Materinya mencakup topik penting seperti pengukuran teknik, jenis-jenis bahan teknik, gaya dan gerak, sistem transmisi, serta pengenalan alat dan mesin kerja. Disusun berdasarkan kurikulum kejuruan terbaru, buku ini menggabungkan teori, ilustrasi teknis, dan latihan praktik bengkel agar siswa dapat memahami konsep dan langsung menerapkannya di lapangan. Setiap bab dilengkapi dengan contoh perhitungan, gambar teknik, soal evaluasi, serta kegiatan proyek sederhana untuk mengasah keterampilan analisis dan ketelitian. Dengan bahasa yang jelas dan pendekatan berbasis kompetensi, buku ini menjadi panduan ideal bagi siswa SMK/MAK kelas X dalam membangun dasar pengetahuan dan keterampilan yang kuat di bidang teknik mesin.'),
(164, 'Dasar-Dasar Otomotif SMK/MAK Kelas X Jilid 1', 'Sutrisno', 'Bumi Aksara', 2025, 20, '164g.jpg', 1, 'Materi dasar otomotif jilid 1 untuk SMK/MAK kelas X, pengantar sistem kendaraan dan teknik otomotif.'),
(165, 'Pembelajaran Unggul Bahasa Indonesia SMK/MAK Kelas X Jilid A', 'Endah Tri Priyatni & Titik Harsiati', 'Bumi Aksara', 2025, 20, '165g.jpg', 1, 'Buku pelajaran unggul bahasa Indonesia SMK/MAK kelas X jilid A; membahas keterampilan berbahasa dan sastra.'),
(166, 'Pembelajaran Unggul Bahasa Indonesia SMK/MAK Kelas XI Jilid A', 'Endah Tri Priyatni & Astria Prameswari', 'Bumi Aksara', 2025, 20, '166g.jpg', 1, 'Buku pelajaran unggul bahasa Indonesia SMK/MAK kelas XI jilid A; memperkuat pemahaman teks dan keterampilan menulis.'),
(167, 'Pembelajaran Unggul English SMK/MAK Kelas X Jilid B', 'Ikhwan Muslim & Dendy Desmal', 'Bumi Aksara', 2025, 20, '167g.jpg', 1, 'Buku ajar Bahasa Inggris SMK/MAK kelas X jilid B dengan pendekatan kontekstual dan latihan keterampilan bahasa.'),
(168, 'Pembelajaran Unggul English SMK/MAK Kelas XI Jilid B', 'Ikhwan Muslim & Dendy Desmal', 'Bumi Aksara', 2025, 20, '168g.jpg', 1, 'Buku Bahasa Inggris untuk SMK/MAK kelas XI jilid B; fokus pada komunikasi lisan dan penulisan akademik.'),
(169, 'Pembelajaran Unggul English SMK/MAK Kelas XII Jilid B', 'Ikhwan Muslim & Dendy Desmal', 'Bumi Aksara', 2025, 20, '169g.jpg', 1, 'Buku Bahasa Inggris jilid B untuk SMK/MAK kelas XII; meningkatkan keterampilan berbahasa untuk persiapan dunia kerja.'),
(170, 'Dasar-Dasar Akuntansi dan Kuangan Lembaga SMK/MAK Kelas X Jilid 1', 'Riris Khuzaemah', 'Bumi Aksara', 2025, 20, '170g.jpg', 1, 'Buku dasar akuntansi dan keuangan lembaga untuk SMK/MAK kelas X jilid 1; pengantar pencatatan transaksi keuangan.'),
(171, 'Dasar-Dasar Akuntansi dan Kuangan Lembaga SMK/MAK Kelas X Jilid 2', 'Riris Khuzaemah', 'Bumi Aksara', 2025, 20, '171g.jpg', 1, 'Lanjutan materi dasar akuntansi dan keuangan lembaga jilid 2; membahas laporan keuangan dan analisis sederhana.'),
(172, 'Pembelajaran Unggul Sejarah SMK/MAK Kelas X Jilid A', 'Brilliantoro Yusuf Ervanda', 'Bumi Aksara', 2025, 20, '172g.jpg', 1, 'Buku sejarah SMK/MAK kelas X jilid A; menyajikan peristiwa penting nasional dan internasional.'),
(173, 'Pembelajaran Unggul Sejarah SMK/MAK Kelas XI Jilid A', 'Brilliantoro Yusuf Ervanda, Pandji Saputra', 'Bumi Aksara', 2025, 20, '173g.jpg', 1, 'Materi sejarah SMK/MAK kelas XI jilid A; mengulas sejarah modern Indonesia dan perkembangan global.'),
(175, 'Dasar-Dasar Teknik Jaringan Komputer dan Telekomunikasi SMK/MAK Kelas X Jilid 2', 'Darsono', 'Bumi Aksara', 2025, 20, '175g.jpg', 1, 'Lanjutan dasar teknik jaringan komputer dan telekomunikasi jilid 2; pembahasan sistem komunikasi dan troubleshooting.'),
(176, 'Proyek IPAS Ilmu Pengetahuan Alam dan Sosial Rumpun Teknoloi SMK/MAK Kelas X', 'Siti Hamamatul Mar\'ati S.Si. dan Ratih Setyari', 'Bumi Aksara', 2022, 20, '176g.jpg', 1, 'Buku proyek IPAS untuk SMK/MAK kelas X; integrasi IPA dan IPS dengan pendekatan teknologi.'),
(177, 'Pendidikan Agama Islam dan Budi Pekerti SMA/SMK Kelas XI', 'Drs. H. Abd. Rahman, MA dan Hery Nugroho, M.S.I., M.Si', 'Pusat Perbukuan', 2022, 40, '177g.jpg', 1, 'Buku Pendidikan Agama Islam kelas XI SMA/SMK; menekankan nilai akhlak dan ibadah.'),
(178, 'KARYA DESAIN (Desain Komunikasi Visual) SMK/MAK Kelas XI dan XII', 'Ayu Sarwendah S.Pd., M.Pd., Gr', 'Liniswara', 2023, 21, 'g241.jpg', 1, 'Buku ini merupakan buku pembelajaran Desain Komunikasi Visual (DKV) yang ditujukan untuk siswa SMK/MAK kelas XI–XII, mencakup teori dasar hingga praktik penerapan dalam dunia desain. Isinya membahas secara komprehensif tentang prinsip-prinsip desain, elemen visual, tipografi, warna, komposisi, dan layout, serta bagaimana menggunakannya untuk menyampaikan pesan secara efektif. Selain teori, buku ini juga memuat latihan praktik dan proyek kreatif yang membantu siswa mengembangkan kemampuan berpikir visual, estetika, dan keterampilan teknis menggunakan perangkat lunak desain. Disusun dengan pendekatan pembelajaran berbasis kompetensi, buku ini mendukung penguasaan kompetensi profesional di bidang desain grafis dan komunikasi visual. Dengan penyajian yang menarik, ilustrasi penuh warna, dan contoh aplikasi nyata, buku ini menjadi panduan ideal bagi siswa SMK/MAK yang ingin memahami dan menguasai dunia desain komunikasi visual secara mendalam.'),
(179, 'Pendidikan Agama Islam dan Budi Pekerti SMA/SMK Kelas XI', 'Ahmad Taufik, S.Pd.I, M.Pd. dan Nurwastuti Setyowati', 'Pusat Kurikulum dan Perbukuan', 2021, 22, 'g251.jpg', 1, 'Buku ini merupakan Buku Pendidikan Agama Islam (PAI) dan Budi Pekerti untuk SMA/SMK kelas XI yang disusun untuk membantu siswa memahami, menghayati, dan mengamalkan ajaran Islam secara komprehensif. Melalui pendekatan pembelajaran kontekstual, buku ini tidak hanya mengajarkan aspek pengetahuan agama seperti akidah, ibadah, akhlak, dan sejarah Islam, tetapi juga menekankan pembentukan karakter religius, disiplin, tanggung jawab, dan toleransi. Setiap bab dilengkapi dengan ayat Al-Qur’an, hadis, kisah teladan, refleksi diri, serta kegiatan pembelajaran aktif yang mendorong siswa untuk menerapkan nilai-nilai Islam dalam kehidupan sehari-hari. Dengan bahasa yang mudah dipahami dan contoh yang relevan dengan dunia remaja, buku ini menjadi panduan penting dalam membentuk pribadi muslim yang beriman, berakhlak mulia, dan berperan positif di masyarakat.'),
(180, 'English In Mind Second Edition Student\'s Book 3', 'Herbert Puchta dan Jeff Strank S', 'Pusat Kurikulum dan Perbukuan', 2021, 55, '180g.jpg', 1, 'Buku English In Mind edisi kedua, student book 3; materi bahasa Inggris tingkat lanjut.'),
(181, 'Komputer dan Jaringan Dasar C2 Program Keahlian Teknik Komputer dan Informatika SMK/MAK Kelas X', 'Andi Novianto, S.Kom., M.Kom', 'Penerbit Erlangga', 2018, 42, '181g.jpg', 1, 'Buku dasar komputer dan jaringan untuk program keahlian TKI kelas X.'),
(182, 'Pembelajaran Unggul PJOK SMK/MAK Kelas X Jilid 1', 'Sugito Adi Warsito dan Hertika Yusniawati', 'Bumi Aksara', 2022, 9, '182g.jpg', 1, 'Buku PJOK jilid 1 untuk SMK/MAK kelas X; pembelajaran jasmani, olahraga dan kesehatan.'),
(183, 'Pembelajaran Unggul PJOK SMK/MAK Kelas XI Jilid 2', 'Sugito Adi Warsito dan Hertika Yusniawati', 'Bumi Aksara', 2022, 9, '183g.jpg', 1, 'Buku PJOK jilid 2 untuk SMK/MAK kelas XI; latihan fisik dan teori kesehatan.'),
(184, 'Perangkat Lunak Desain (Desain Komunikasi Visual) SMK/MAK Kelas XI dan XII', 'Ira Luvi Indah Astutik', 'Liniswara', 2023, 19, '184g.jpg', 1, 'Buku perangkat lunak desain untuk komunikasi visual di SMK/MAK kelas XI-XII.'),
(185, 'Teknik Komputer Jaringan SMK/MAK Kelas XII', 'Fauziah S.Kom., MMSI', 'Yudhistira', 2012, 32, '185g.jpg', 1, 'Buku teknik komputer jaringan untuk SMK/MAK kelas XII; teori dan praktik administrasi jaringan.'),
(186, 'Pemrograman Web SMK Kelas X', 'Heriyanto, S.Kom., Gito Rolis, S.Kom. dan Iky Tri P', 'Yudhistira', 2014, 30, '186g.jpg', 1, 'Buku dasar pemrograman web untuk SMK kelas X; pengantar HTML, CSS, dan pemrograman.'),
(187, 'Matematika SMA/SMK Kelas XI Jilid 2', 'Dicky Susanto dkk', 'Pusat Perbukuan', 2021, 79, '187g.jpg', 1, 'Buku Matematika SMA/SMK kelas XI jilid 2; berisi konsep, latihan, dan soal evaluasi.'),
(188, 'Bahasa Jepang Untuk SMA/MA Kelas XII', 'Evi Lusiana, Igarashi Yuka, Mitsumoto Tomoya, Hatta Naomi, Furuuchi Ayako', 'Penerbit Erlangga', 2017, 14, '188g.jpg', 1, 'Buku Bahasa Jepang untuk SMA/MA kelas XII; fokus percakapan dan tata bahasa tingkat lanjut.'),
(189, 'Rekayasa Perangkat Lunak SMK Kelas X', 'Bambang Jatmika, S.ST., MT', 'Yudhistira', 2010, 23, '189g.jpg', 1, 'Buku rekayasa perangkat lunak untuk SMK kelas X; membahas dasar RPL dan siklus pengembangan software.'),
(190, 'Pendidikan Pancasila dan Kewarganegaraan SMK/MA Kelas X', 'Dwi Winarto, S.Pd.', 'Bumi Aksara', 1990, 10, '190g.jpg', 1, 'Buku PPKn untuk SMK/MA kelas X; membahas nilai kebangsaan, hukum, dan demokrasi.'),
(191, 'Bahasa Inggris SMA/MA/SMK/MAK Kelas X Semester 2', 'Utami Widiati, Zuliati Rohmah, dan Furaidah', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2014, 6, '191g.jpg', 1, 'Buku Bahasa Inggris untuk SMA/MA/SMK/MAK kelas X semester 2; materi lanjutan bahasa Inggris.'),
(192, 'Forward An English Course For Vocational School Students Grade X', 'Shyla K. Lande dan Eka Mulya Astuti', 'Penerbit Erlangga', 2016, 1, '192g.jpg', 1, 'Buku kursus bahasa Inggris untuk siswa SMK/MAK kelas X; disusun berbasis kompetensi.'),
(193, 'Forward An English Course For Vocational School Students Grade XI', 'Shyla K. Lande dan Eka Mulya Astuti', 'Penerbit Erlangga', 2017, 4, 'g255.jpg', 1, 'Buku ini merupakan Buku Kursus Bahasa Inggris untuk siswa SMK/MAK kelas XI yang dirancang untuk meningkatkan keterampilan komunikasi lisan dan tulisan dalam konteks akademik maupun profesional. Materi dalam buku ini mencakup empat aspek utama — listening, speaking, reading, dan writing — yang dikemas dalam bentuk dialog interaktif, teks autentik, latihan percakapan, serta tugas proyek komunikatif. Setiap bab mengangkat tema-tema kontekstual seperti kehidupan sekolah, pekerjaan, teknologi, dan budaya internasional, sehingga membantu siswa beradaptasi dengan situasi komunikasi nyata di dunia kerja. Buku ini juga dilengkapi dengan grammar focus, vocabulary enrichment, pronunciation guide, dan evaluasi kompetensi untuk memperkuat pemahaman bahasa. Dengan pendekatan communicative learning dan aktivitas kolaboratif, buku ini menjadi panduan efektif bagi siswa SMK/MAK kelas XI untuk berkomunikasi dalam Bahasa Inggris secara percaya diri, aktif, dan profesional.'),
(194, 'Matematika SMK/MAK Kelas XII', 'Drs. Kasmina, M.Sc. dan Drs. Toali, MM.Pd.', 'Penerbit Erlangga', 2015, 1, '194g.jpg', 1, 'Buku Matematika SMK/MAK kelas XII; mencakup teori, contoh soal, dan latihan evaluasi.'),
(195, 'Teknik Pemeliharaan dan Perbaikan Sistem Elektronika Jilid 3', 'Peni Handayani dkk.', 'Direktorat Pembinaan Sekolah Menengah Kejuruan', 2008, 1, '195g.jpg', 1, 'Buku teknik pemeliharaan dan perbaikan sistem elektronika jilid 3 untuk SMK.'),
(196, 'Pemrograman Dasar SMK/MAK Kelas X', 'Abdul Munif, S.Pd., S.ST., M.Kom dan Dhanang Fitria Riaji, M.T', 'Pt. Latif Kitto Mahesa', 2016, 1, '196g.jpg', 1, 'Buku dasar pemrograman untuk SMK/MAK kelas X; membahas konsep logika dan bahasa pemrograman.'),
(197, 'Pendidikan Jasmani Olahraga dan Kesehatan SMA/MA/SMK/MAK Kelas XII', 'Soemaryoto dan Soni Nopembri', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2015, 1, '197g.jpg', 1, 'Buku PJOK untuk SMA/MA/SMK/MAK kelas XII; mencakup teori kesehatan dan praktik olahraga.'),
(198, 'Cerdas Cergas Berbahasa dan Bersastra Indonesia SMA/SMK Kelas XI', 'Heny Marwati dan K. Waskitaningtyas', 'Pusat Perbukuan', 2021, 1, '198g.jpg', 1, 'Buku Bahasa Indonesia untuk SMA/SMK kelas XI; mengasah keterampilan berbahasa dan bersastra.'),
(199, 'Teknik Komputer Jaringan SMK Kelas X', 'Har Akbar, S.Kom., S.E. dan Ginung Yogiswastiko, S.Si.', 'Yudhistira', 2011, 1, '199g.jpg', 1, 'Buku dasar teknik komputer jaringan untuk SMK kelas X; pengantar jaringan komputer.'),
(200, 'Sistem Operasi SMK Kelas X', 'Heriyanyo, Gito Rolis, dan Hermas Andriyanto', 'Yudhistira', 2014, 1, '200g.jpg', 1, 'Buku Sistem Operasi untuk SMK kelas X; teori dasar, instalasi, dan pengelolaan OS.'),
(201, 'Fisika Kelas X', 'Drs. K. Kamajaya, M.Sc.', 'Grafindo Media Pratama', 2010, 1, '201g.jpg', 1, 'Buku Fisika SMA/SMK kelas X; membahas konsep dasar fisika dengan contoh dan latihan.'),
(202, 'Communication Builder Vocational School SMK/MAK Grade XII', 'Eri Kurniawan dan Arief Kurniawan', 'Pusat Perbukuan', 2008, 3, 'g256.jpg', 1, 'Buku ini merupakan Buku Communication Builder untuk SMK/MAK kelas XII yang dirancang khusus untuk memperkuat keterampilan komunikasi Bahasa Inggris siswa di tingkat lanjut. Dengan pendekatan berbasis kompetensi, buku ini menekankan penguasaan empat keterampilan utama — listening, speaking, reading, dan writing — melalui berbagai dialog realistis, teks profesional, latihan berbasis situasi kerja, serta proyek komunikasi antar siswa. Materi disusun secara kontekstual agar siswa mampu berinteraksi secara efektif dalam lingkungan akademik maupun dunia industri. Selain itu, buku ini dilengkapi dengan grammar review, vocabulary expansion, pronunciation tips, serta evaluasi berbasis tugas (task-based assessment) untuk mengasah kemampuan berbahasa yang komunikatif dan tepat sasaran. Dengan penyajian yang interaktif, ilustrasi menarik, dan contoh penggunaan bahasa dalam konteks nyata, buku ini menjadi sumber belajar ideal bagi siswa SMK/MAK kelas XII untuk membangun kepercayaan diri dan kemampuan komunikasi Bahasa Inggris yang profesional dan global.'),
(203, 'Bahasa Inggris SMK/MAK Kelas XI', 'Mahrukh Bashir', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2014, 2, '203g.jpg', 1, 'Buku Bahasa Inggris untuk SMK/MAK kelas XI; disusun sesuai kurikulum pendidikan nasional.'),
(204, 'Bahasa Inggris SMK/MAK Kelas XI', 'Entin Sutinah, dkk.', 'Penerbit Erlangga', 2010, 1, '204g.jpg', 1, 'Buku Bahasa Inggris untuk SMK/MAK kelas XI; materi komunikasi, tata bahasa, dan latihan soal.'),
(205, 'Seni Budaya SMA/SMK Kelas XI Semester 1', 'Sem Cornelyoes Bangun, Siswandi, Tati Narawati, Jose Rizal Manua', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2014, 1, 'g254.jpg', 1, 'Buku ini merupakan Buku Seni Budaya untuk SMA/SMK kelas XI semester 1 yang menyajikan pembelajaran terpadu mengenai empat cabang utama seni, yaitu seni rupa, seni musik, seni tari, dan seni teater. Disusun berdasarkan kurikulum pendidikan terkini, buku ini menggabungkan konsep teori, apresiasi, dan praktik berkesenian untuk menumbuhkan kreativitas, kepekaan estetika, dan kemampuan berekspresi siswa. Setiap bab dilengkapi dengan penjelasan konsep, contoh karya, biografi seniman, aktivitas praktik, serta proyek kolaboratif yang mendorong pembelajaran aktif dan menyenangkan. Buku ini juga mengajak siswa memahami peran seni dalam kehidupan sosial dan budaya Indonesia, serta mengembangkan apresiasi terhadap keberagaman seni daerah dan modern. Dengan gaya bahasa yang komunikatif dan ilustrasi menarik, buku ini menjadi panduan ideal bagi siswa SMA/SMK kelas XI dalam mengasah kemampuan artistik dan memperluas wawasan budaya.');
INSERT INTO `buku` (`id`, `judul`, `pengarang`, `penerbit`, `tahun`, `quantity`, `gambar`, `id_kategori`, `deskripsi`) VALUES
(206, 'Kimia SMK/MAK Kelas X', 'Dra. Nefoyati, MM.', 'HUP', 2018, 1, 'g258.jpg', 1, 'Buku ini merupakan Buku Kimia untuk SMK/MAK kelas X yang membahas secara menyeluruh dasar-dasar ilmu kimia serta penerapannya dalam kehidupan sehari-hari dan dunia kerja. Materi disusun berdasarkan kurikulum terbaru dengan pendekatan ilmiah, kontekstual, dan aplikatif, mencakup topik-topik seperti struktur atom, tabel periodik unsur, ikatan kimia, reaksi kimia, hukum dasar kimia, serta pengukuran dan perhitungan kimia. Setiap bab dilengkapi dengan contoh soal, eksperimen sederhana, ilustrasi konsep, dan latihan evaluasi yang membantu siswa memahami teori sekaligus mengasah keterampilan praktikum. Buku ini juga menyoroti peran kimia dalam industri, lingkungan, dan teknologi, sehingga siswa dapat melihat relevansi antara ilmu kimia dan bidang kejuruan mereka. Dengan penjelasan yang runtut, bahasa yang mudah dipahami, dan kegiatan pembelajaran berbasis praktik, buku ini menjadi panduan ideal bagi siswa SMK/MAK kelas X untuk membangun pemahaman kuat tentang ilmu kimia dasar dan aplikasinya.'),
(207, 'Erlangga X-Press UN SMK/MAK 2020 Bahasa Indonesia', 'Rustamaji', 'Penerbit Erlangga', 2019, 3, 'g257.jpg', 1, 'Buku ini merupakan Buku Persiapan Ujian Nasional (UN) Bahasa Indonesia untuk SMK/MAK Tahun 2020 yang disusun untuk membantu siswa menghadapi ujian dengan strategi belajar efektif, latihan soal lengkap, dan pembahasan mendalam. Materinya mencakup seluruh kompetensi dasar Bahasa Indonesia yang diujikan, seperti pemahaman teks bacaan, penulisan paragraf, kebahasaan, serta analisis isi dan struktur teks. Buku ini dilengkapi dengan rangkuman materi ringkas, contoh soal UN tahun-tahun sebelumnya, prediksi soal terbaru, serta pembahasan langkah demi langkah agar siswa mudah memahami tipe soal dan cara menjawabnya dengan benar. Selain itu, terdapat simulasi ujian dan lembar evaluasi mandiri untuk mengukur kesiapan belajar. Dengan bahasa yang jelas, format yang sistematis, dan penekanan pada teknik menjawab cepat dan tepat, buku ini menjadi panduan belajar praktis bagi siswa SMK/MAK dalam mempersiapkan diri menghadapi Ujian Nasional Bahasa Indonesia dengan percaya diri dan hasil maksimal.'),
(208, 'Sejarah Nasional dan Sejarah Umum SMK Kelas X', 'Drs. Nurcholis B. Yunani dan Drs. Dudi Sudiman', 'Ankasa Bandung', 2000, 1, '208g.jpg', 1, 'Buku Sejarah Nasional dan Umum untuk SMK kelas X; menyajikan sejarah Indonesia dan dunia.'),
(209, 'Pendidikan Pancasila dan Kewarganegaraan SMK/MA Kelas X', 'Rochimudin', 'Bumi Aksara', 2022, 1, 'g253.jpg', 1, 'Buku ini merupakan Buku Pendidikan Pancasila dan Kewarganegaraan (PPKn) untuk SMK/MA kelas X yang dirancang untuk menanamkan nilai-nilai Pancasila, semangat kebangsaan, dan tanggung jawab sebagai warga negara Indonesia. Materinya mencakup topik-topik penting seperti konsep dasar Pancasila, konstitusi dan hukum, hak dan kewajiban warga negara, nasionalisme, serta partisipasi dalam kehidupan demokratis. Setiap bab disusun secara sistematis dengan penjelasan konsep, studi kasus aktual, refleksi nilai, dan kegiatan diskusi kelas yang mendorong siswa berpikir kritis dan berkarakter. Buku ini juga menekankan pentingnya pengamalan nilai moral dan etika Pancasila dalam kehidupan sehari-hari, baik di lingkungan sekolah, keluarga, maupun masyarakat. Dengan bahasa yang komunikatif dan contoh kontekstual, buku ini menjadi panduan pembelajaran ideal bagi siswa SMK/MA kelas X untuk membentuk pribadi yang cerdas, berintegritas, dan berjiwa kebangsaan.'),
(210, 'Pendidikan Pancasila dan Kewarganegaraan SMK/MA Kelas XI', 'Rochimudin', 'Bumi Aksara', 2022, 1, '210g.jpg', 1, 'Buku PPKn untuk SMK/MA kelas XI; fokus pada pemahaman konstitusi dan kehidupan demokrasi.'),
(211, 'Pendidikan Pancasila dan Kewarganegaraan SMA/SMK Kelas X', 'Abdul Waidl, dkk.', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2021, 1, '211g.jpg', 1, 'Buku PPKn untuk SMA/SMK kelas X; membahas dasar negara, konstitusi, dan hak-hak warga negara.'),
(212, 'Teknologi Jaringan Berbasis Luas (WAN) Kelas XI', 'Rudy Setiawan', 'Mediatama', 2018, 1, 'g259.jpg', 1, 'Buku ini merupakan Buku Teknologi Jaringan Berbasis Luas (WAN) untuk SMK/MAK kelas XI yang membahas secara komprehensif konsep dasar, teori, dan praktik penerapan jaringan komputer berskala luas. Disusun sesuai dengan kurikulum terbaru bidang Teknik Komputer dan Jaringan (TKJ), buku ini menjelaskan materi penting seperti arsitektur jaringan WAN, perangkat jaringan (router, switch, modem), topologi jaringan, protokol komunikasi, routing dinamis dan statis, serta keamanan jaringan. Selain teori, buku ini juga dilengkapi dengan panduan praktik konfigurasi jaringan menggunakan perangkat fisik maupun simulasi (seperti Cisco Packet Tracer), studi kasus, serta latihan proyek berbasis kompetensi. Setiap bab diakhiri dengan rangkuman, latihan soal, dan evaluasi kinerja siswa untuk memperkuat pemahaman. Dengan bahasa yang mudah dipahami, ilustrasi teknis, dan pendekatan praktik langsung, buku ini menjadi panduan ideal bagi siswa SMK/MAK kelas XI dalam mempelajari dan menguasai keterampilan membangun serta mengelola jaringan WAN secara profesional.'),
(213, 'SBSB Sastrawan Bicara Siswa Bertanya 2015', 'Rendra, dkk.', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2015, 4, 'g260.jpg', 4, 'Buku ini merupakan buku nonfiksi yang berisi kumpulan tanya jawab antara siswa dan para sastrawan Indonesia, disusun untuk menumbuhkan apresiasi terhadap karya sastra dan dunia kepenulisan. Melalui percakapan yang ringan namun bermakna, buku ini menghadirkan beragam pandangan sastrawan tentang proses kreatif, makna karya sastra, perjuangan dalam menulis, serta relevansi sastra dalam kehidupan modern. Setiap sesi tanya jawab memuat pertanyaan reflektif dari siswa dan jawaban inspiratif dari para penulis, penyair, dan novelis ternama, sehingga pembaca dapat memahami nilai-nilai moral, budaya, dan kemanusiaan yang terkandung dalam sastra. Buku ini juga dilengkapi dengan pengantar biografi singkat para sastrawan, kutipan karya, dan kegiatan refleksi, menjadikannya sumber belajar menarik bagi siswa, guru, maupun pecinta sastra yang ingin memperdalam pemahaman dan kecintaan terhadap dunia sastra Indonesia.'),
(214, 'Martematika SMA/SMK Kelas XII', 'Abdur Rahman As\'ari, dkk.', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2015, 2, '214g.jpg', 2, 'Buku Matematika SMA/SMK kelas XII yang membahas konsep, teori, dan latihan soal sesuai kurikulum nasional.'),
(215, 'Matematika SMK/MAK Kelas XII', 'Kasmina dan Toali', 'Penerbit Erlangga', 2018, 3, '215g.jpg', 1, 'Buku Matematika SMK/MAK kelas XII; berisi materi aljabar, trigonometri, kalkulus, dan soal latihan.'),
(216, 'Matematika SMK/MAK Kelas XII', 'Yuliatun Aisyah', 'Bumi Aksara', 2018, 2, '216g.jpg', 1, 'Buku Matematika SMK/MAK kelas XII; membahas konsep matematika terapan untuk pendidikan vokasi.'),
(217, 'Multimedia Teknik Animasi 2D dan 3D SMK/MAK Kelas 2', 'Hanifah Wijayanti', 'Putra Nugraha', 2019, 17, '217g.jpg', 1, 'Buku Multimedia untuk SMK/MAK kelas XI; fokus pada teknik animasi 2D dan 3D serta aplikasi praktisnya.'),
(218, 'Matematika SMA/SMK Kelas X Semester 1', 'B.K. Noormandiri', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2014, 1, '218g.jpg', 1, 'Buku Matematika untuk SMA/SMK kelas X semester 1 sesuai kurikulum nasional, berisi konsep dasar dan latihan soal.'),
(219, 'Matematika SMA/SMK Kelas X Semester 2', 'Bornok Sinaga, dkk.', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2014, 1, '219g.jpg', 1, 'Buku Matematika SMA/SMK kelas X semester 2 melanjutkan pembahasan konsep aljabar, geometri, dan trigonometri.'),
(220, 'Pemrograman Dasar SMK Kelas 1', 'Ardes Widiyanto', 'Yudhistira', 2014, 2, 'g262.jpg', 1, 'Buku ini merupakan Buku Pelajaran Dasar Pemrograman untuk SMK kelas X yang dirancang untuk memperkenalkan konsep-konsep fundamental dalam dunia pemrograman komputer secara teori dan praktik. Disusun sesuai dengan kurikulum terbaru bidang Rekayasa Perangkat Lunak (RPL) dan Teknik Komputer dan Jaringan (TKJ), buku ini membahas topik-topik penting seperti logika pemrograman, algoritma dasar, struktur data sederhana, serta pengenalan bahasa pemrograman populer seperti Python, C++, atau Java.\r\n\r\nSetiap bab dilengkapi dengan contoh kode, penjelasan langkah demi langkah, latihan proyek mini, serta studi kasus aplikasi sederhana yang membantu siswa memahami penerapan logika dalam pembuatan program. Buku ini juga menekankan pemecahan masalah (problem solving), penulisan kode yang efisien, dan penerapan konsep pemrograman berorientasi objek. Dengan bahasa yang mudah dipahami, ilustrasi alur logika, dan latihan mandiri, buku ini menjadi panduan ideal bagi siswa SMK kelas X untuk membangun dasar yang kuat dalam pemrograman dan mempersiapkan diri menuju pengembangan aplikasi nyata di dunia industri.'),
(221, 'Fisika SMK/SMA Kelas X', 'Melisa, S.Pd, M.PFis', 'HUP', 2018, 2, '221g.jpg', 1, 'Buku Fisika untuk SMA/SMK kelas X yang membahas konsep dasar fisika dan penerapannya.'),
(222, 'Sejarah Indonesia SMA/SMK Kelas XI', 'Sardiman AM dan Amurwani Dwi Lestariningsih', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2017, 3, 'g261.jpg', 1, 'Buku ini merupakan Buku Sejarah Indonesia untuk SMA/SMK kelas XI yang membahas secara mendalam peristiwa-peristiwa penting dalam sejarah nasional Indonesia dari masa penjajahan hingga era kemerdekaan dan pembangunan bangsa. Disusun berdasarkan kurikulum terbaru, buku ini menguraikan perkembangan politik, ekonomi, sosial, dan budaya yang membentuk identitas bangsa Indonesia. Materi mencakup topik seperti perlawanan rakyat terhadap kolonialisme, kebangkitan nasional, proklamasi kemerdekaan, serta perjuangan mempertahankan kedaulatan negara. Setiap bab dilengkapi dengan sumber sejarah, peta, foto dokumentasi, dan aktivitas analisis sejarah untuk melatih kemampuan berpikir kritis dan historis siswa. Buku ini juga mengaitkan peristiwa masa lalu dengan relevansinya terhadap kehidupan berbangsa dan bernegara masa kini. Dengan bahasa yang jelas, penyajian menarik, dan pendekatan kontekstual, buku ini menjadi panduan belajar ideal bagi siswa SMA/SMK kelas XI untuk memahami perjalanan panjang sejarah Indonesia dan menumbuhkan semangat nasionalisme.'),
(223, 'Sejarah Indonesia SMA/SMK Kelas XII', 'Abdurakhman, dkk.', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2015, 1, '223g.jpg', 1, 'Buku Sejarah Indonesia SMA/SMK kelas XII sesuai kurikulum, dengan fokus pada sejarah modern Indonesia.'),
(224, 'Bahasa Jepang Untuk SMA/MA Kelas XI 2', 'Evi Lusiana, dkk.', 'Penerbit Erlangga', 2017, 2, 'g233.jpg', 1, 'Buku ini merupakan buku pembelajaran Bahasa Jepang untuk siswa SMA/MA kelas XI yang disusun secara sistematis sesuai kurikulum pendidikan. Isinya mencakup tata bahasa dasar, kosakata tematik, serta latihan percakapan sehari-hari yang membantu siswa berkomunikasi dalam situasi sederhana dengan bahasa yang benar. Setiap bab dilengkapi dengan contoh kalimat, latihan membaca dan menulis huruf hiragana, katakana, serta pengenalan dasar kanji. Buku ini juga menyertakan ilustrasi, latihan interaktif, dan dialog kontekstual agar proses belajar terasa lebih menarik dan mudah diikuti. Dengan pendekatan komunikatif, buku ini tidak hanya mengajarkan struktur bahasa, tetapi juga memperkenalkan budaya Jepang secara singkat untuk memperkaya pemahaman siswa. Cocok digunakan sebagai buku teks utama maupun pendamping belajar mandiri bagi siswa yang ingin menguasai dasar-dasar Bahasa Jepang secara efektif.'),
(225, 'Fisika Jilid 2 SMK', 'Endarko, dkk.', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2010, 2, '225g.jpg', 1, 'Buku Fisika jilid 2 untuk SMK, membahas konsep fisika lanjutan dengan soal latihan.'),
(226, 'Somewhere I\'ll Find You', 'Lisa Kleypas', 'Kompas Gramedia', 2013, 1, '226g.jpg', 3, 'Novel romantis karya Lisa Kleypas yang mengisahkan cinta, intrik, dan perjalanan emosional tokoh utamanya.'),
(227, 'Dollhouse', 'Kourtney, Kim dan Khloe Kardashian', 'Esensi', 2011, 1, '227g.jpg', 3, 'Novel fiksi tentang kehidupan keluarga Kardashian yang penuh drama dan ambisi.'),
(228, 'Suami Pilihan', 'Leigh Michaels', 'PT Gramedia Pustaka Utama', 2004, 1, '228g.jpg', 3, 'Novel romantis karya Leigh Michaels yang mengisahkan dilema cinta dan pilihan hidup.'),
(229, 'Rahasia Muslimah Idaman', 'Adil Fathi Addullah', 'Qultum Media', 2007, 1, '229g.jpg', 2, 'Buku non fiksi Islami yang memberikan panduan dan inspirasi menjadi muslimah ideal.'),
(230, 'Seni Merayu Tuhan', 'Husein Ja\'far Al-Hadar', 'Mizan Media Utama', 2022, 3, '230g.jpg', 3, 'Novel religi inspiratif yang mengajak pembaca untuk mendekatkan diri kepada Tuhan melalui kisah penuh makna.'),
(231, 'Fisika SMK/SMA Kelas X', 'Melisa Cahyadi, S.Pd, M.PFis', 'HUP', 2020, 2, '231g.jpg', 1, 'Buku Fisika untuk SMK/SMA kelas X edisi terbaru dengan penjelasan konsep dasar dan latihan soal.'),
(232, 'Fisika Jilid 2 SMK', 'Endarko, dkk', 'Direktorat Pembinaan Sekolah Menengah Kejuruan', 2008, 2, '232g.jpg', 1, 'Buku Fisika jilid 2 untuk SMK, membahas materi lanjutan fisika terapan sesuai kurikulum.'),
(233, 'English in Mind Second Edition', 'Brian Hert with Mario Rinvolucri, Herbert Puchta & Jeff Stranks', 'Pusat Kurikulum dan Perbukuan', 2021, 2, 'g250.jpg', 1, 'Buku ini merupakan Buku Bahasa Inggris edisi kedua untuk SMK/SMA yang dirancang khusus untuk meningkatkan keterampilan komunikasi siswa dalam konteks akademik dan kehidupan sehari-hari. Materi dalam buku ini mencakup empat kemampuan utama bahasa Inggris — listening, speaking, reading, dan writing — yang disajikan melalui berbagai dialog, teks bacaan autentik, latihan percakapan, serta tugas proyek interaktif. Setiap bab berfokus pada tema-tema relevan seperti pendidikan, teknologi, lingkungan, dan budaya, sehingga mendorong siswa untuk berpikir kritis dan komunikatif. Buku ini juga dilengkapi dengan grammar focus, vocabulary builder, pronunciation guide, dan latihan evaluasi di akhir bab untuk memperkuat pemahaman. Dengan pendekatan communicative learning dan aktivitas berbasis situasi nyata, buku ini menjadi panduan efektif bagi siswa SMK/SMA dalam menguasai Bahasa Inggris secara aktif, percaya diri, dan kontekstual sesuai tuntutan dunia modern.'),
(234, 'Teknik Komputer Jaringan SMK Kelas X', 'Har Akbar, S.Kom, S.E & Ginung Yogiswastiko, S.Si', 'Yudhistira', 2011, 1, '234g.jpg', 1, 'Buku dasar jaringan komputer untuk SMK kelas X yang membahas konsep jaringan, hardware, dan software.'),
(235, 'Seni Budaya SMA/SMK Kelas XII Semester 1', 'Agusbudiman, Dewi Suryati Budiwati, Sukanta, dan Zakaria S. Soetedja', 'Kementerian Pendidikan dan Kebudayaan', 2015, 1, 'g249.jpg', 1, 'Buku ini merupakan Buku Seni Budaya untuk SMA/SMK kelas XII semester 1 yang membahas secara mendalam empat bidang utama seni, yaitu seni musik, seni tari, seni rupa, dan seni teater. Disusun berdasarkan kurikulum terbaru, buku ini tidak hanya mengulas teori, sejarah, dan konsep estetika, tetapi juga mendorong praktik kreatif dan apresiasi seni secara langsung. Setiap bab disertai dengan contoh karya, biografi seniman terkenal, langkah-langkah berkarya, serta proyek kolaboratif yang menumbuhkan kemampuan artistik dan kerja sama siswa. Buku ini juga mengajak siswa untuk memahami makna seni dalam konteks budaya lokal dan global, serta mengembangkan kemampuan berkomunikasi dan berekspresi melalui media seni. Dengan bahasa yang komunikatif, ilustrasi menarik, dan panduan pembelajaran aktif, buku ini menjadi sumber belajar ideal bagi siswa kelas XII untuk memperdalam apresiasi dan keterampilan seni menjelang akhir masa studi mereka di SMA atau SMK.'),
(236, 'Bahasa Inggris SMA/MA/SMK/MAK Kelas XI Semester 1', 'Mahrukh Bashir', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2014, 1, '236g.jpg', 1, 'Buku Bahasa Inggris kelas XI semester 1 yang berfokus pada keterampilan listening, speaking, reading, dan writing.'),
(237, 'Ilmu Pengetahuan Sosial SMK Kelas X Semester 1 dan 2', 'E. Juhana Wijaya, Sri Sulestari, Atep Adya Barata', 'CV AMRICO', 2007, 1, 'g247.jpg', 1, 'Buku ini merupakan Buku Pelajaran Ilmu Pengetahuan Sosial (IPS) untuk SMK kelas X semester 1 dan 2 yang disusun berdasarkan kurikulum pendidikan terbaru. Isinya mencakup pembahasan komprehensif mengenai geografi, sejarah, ekonomi, dan sosiologi, yang dikaitkan langsung dengan kehidupan sehari-hari serta dunia kerja. Setiap bab disusun secara sistematis, dimulai dari konsep dasar hingga penerapannya dalam konteks sosial dan lingkungan sekitar. Buku ini juga dilengkapi dengan peta konsep, ilustrasi menarik, tabel data, latihan soal, dan studi kasus untuk membantu siswa memahami fenomena sosial secara lebih mendalam. Dengan pendekatan kontekstual dan interaktif, buku ini mendorong siswa untuk berpikir kritis, peduli terhadap lingkungan sosial, dan memahami peran mereka sebagai bagian dari masyarakat. Cocok digunakan sebagai panduan belajar utama di sekolah maupun bahan pendukung pembelajaran mandiri bagi siswa SMK.'),
(238, 'Seni Budaya SMK Kelas X Semester 1 dan 2', 'Harry D. Fauzi', 'CV AMRICO', 2006, 1, '238g.jpg', 1, 'Buku Seni Budaya untuk SMK kelas X semester 1 dan 2, mencakup seni rupa, musik, tari, dan teater.'),
(239, 'Kewirausahaan SMK Tingkat 3', 'H. Ating Tedjasutisna, MBA', 'CV AMRICO', 2001, 1, 'g248.jpg', 1, 'Buku ini merupakan Buku Kewirausahaan untuk SMK tingkat 3 yang memberikan dasar teori dan praktik dalam dunia bisnis secara komprehensif dan aplikatif. Disusun sesuai dengan kurikulum pendidikan kejuruan, buku ini membahas konsep penting seperti jiwa wirausaha, perencanaan usaha, manajemen pemasaran, keuangan, produksi, serta strategi pengembangan bisnis. Setiap bab dilengkapi dengan contoh kasus nyata, latihan proyek kewirausahaan, dan panduan pembuatan rencana bisnis (business plan) agar siswa dapat langsung menerapkan ilmu yang dipelajari. Buku ini juga menekankan pentingnya inovasi, etika bisnis, dan pemanfaatan teknologi digital dalam menghadapi tantangan dunia usaha modern. Dengan penyajian yang menarik, bahasa yang mudah dipahami, dan pendekatan berbasis praktik, buku ini menjadi panduan ideal bagi siswa SMK tingkat akhir untuk menumbuhkan semangat wirausaha dan mempersiapkan diri menjadi pelaku bisnis mandiri yang kreatif dan profesional.'),
(240, 'Seni Budaya SMA/MA/SMK/MAK Kelas X', 'Zackaria Soetedja, dkk', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2014, 1, 'g246.jpg', 1, 'Buku ini merupakan Buku Seni Budaya untuk SMA/SMK kelas X yang membahas secara menyeluruh empat cabang utama seni, yaitu seni rupa, seni tari, seni musik, dan seni teater. Disusun berdasarkan kurikulum pendidikan terkini, buku ini menggabungkan konsep teori, apresiasi, dan praktik berkesenian agar siswa dapat memahami sekaligus mengekspresikan kreativitas mereka secara seimbang. Setiap bab dilengkapi dengan contoh karya seni, tokoh-tokoh penting, lembar aktivitas, serta tugas proyek yang mendorong pembelajaran interaktif dan kolaboratif. Buku ini juga mengajarkan nilai-nilai estetika, budaya, dan karakter bangsa melalui pengenalan beragam seni daerah Indonesia. Dengan bahasa yang komunikatif, ilustrasi menarik, dan panduan langkah demi langkah dalam berkarya, buku ini menjadi sumber belajar ideal bagi siswa SMA/SMK untuk mengembangkan apresiasi dan keterampilan di bidang seni budaya.'),
(241, 'Teknik Kendaraan Ringan SMK/MAK Kelas XI', 'Nanang Sopyan Riyadi, Wahyu Eko Widiyatmoko', 'CV MEDIATAMA', 2023, 1, '241g.jpg', 1, 'Buku Teknik Kendaraan Ringan untuk SMK kelas XI yang membahas perawatan dan perbaikan mesin otomotif.'),
(242, 'Fisika 1 SMK Kelas X Semester 1 dan 2', 'M. Suratman', 'CV AMRICO', 2006, 2, 'g244.jpg', 1, 'Buku ini merupakan Buku Fisika 1 untuk SMK kelas X semester 1 dan 2 yang disusun sesuai dengan kurikulum pendidikan terkini. Isinya mencakup pembahasan teori dasar fisika seperti besaran dan satuan, gerak, gaya, energi, usaha, suhu, kalor, dan konsep dasar gelombang. Setiap bab disajikan secara sistematis dengan penjelasan konsep yang mudah dipahami, disertai contoh soal, latihan, serta kegiatan eksperimen sederhana untuk memperkuat pemahaman siswa. Buku ini juga menampilkan ilustrasi dan tabel pendukung agar siswa dapat mengaitkan teori dengan penerapan dalam kehidupan sehari-hari maupun dunia kerja. Dengan kombinasi antara materi konseptual dan latihan aplikatif, buku ini menjadi panduan belajar ideal bagi siswa SMK untuk membangun dasar pengetahuan fisika yang kuat dan logis.'),
(243, 'Fisika 2 SMK Kelas XI Semester 1 dan 2', 'M. Suratman', 'CV AMRICO', 2007, 1, 'g245.jpg', 1, 'Buku ini merupakan Buku Fisika untuk SMK kelas XI semester 1 dan 2 yang membahas teori fisika sekaligus penerapannya dalam kehidupan sehari-hari. Disusun secara sistematis sesuai kurikulum, buku ini mencakup materi penting seperti listrik dinamis dan statis, gelombang, bunyi, optika, serta mekanika fluida. Setiap bab dilengkapi dengan penjelasan konsep dasar, contoh penerapan nyata di bidang teknologi dan industri, serta soal latihan dan kegiatan eksperimen sederhana untuk memperkuat pemahaman siswa. Dengan bahasa yang jelas dan ilustrasi pendukung, buku ini membantu siswa mengaitkan ilmu fisika dengan fenomena di sekitar mereka. Didesain untuk mendukung pembelajaran aktif dan kontekstual, buku ini menjadi panduan belajar yang efektif bagi siswa SMK dalam memahami prinsip-prinsip fisika dan manfaatnya dalam kehidupan serta dunia kerja.'),
(244, 'Matematika SMA/SMK Kelas X', 'Dicky Susanto, dkk.', 'Kementerian Pendidikan, Kebudayaan, Riset, dan Teknologi Republik Indonesia', 2021, 1, '244g.jpg', 1, 'Buku Matematika kelas X edisi terbaru sesuai kurikulum Merdeka, berisi materi dasar aljabar, geometri, dan trigonometri.'),
(245, 'Sejarah SMK/MAK Kelas XI Semester 2', 'Ratna Hapsari & M. Adil', 'Erlangga', 2021, 1, '245g.jpg', 1, 'Buku pelajaran Sejarah untuk SMK/MAK kelas XI semester 2 yang membahas perkembangan bangsa Indonesia.'),
(246, 'Ilmu Pengetahuan Alam SMP/MTs Kelas VIII', 'TIM ABDI GURU', 'Erlangga', 2021, 1, '246g.jpg', 1, 'Buku IPA untuk SMP/MTs kelas VIII yang membahas konsep fisika, kimia, dan biologi.'),
(247, 'Matematika SMA/SMK Kelas X', 'Dicky Susanto, dkk.', 'Pusat Kurikulum dan Perbukuan', 2021, 1, '247g.jpg', 1, 'Buku Matematika kelas X terbitan Pusat Kurikulum dan Perbukuan dengan latihan soal berbasis kompetensi.'),
(248, 'Fokus AKM (Asesmen Kompetensi Minimum) SMK/MAK', 'Pipit Dwi Komariah, S.S., M.Pd., dkk.', 'Erlangga', 2020, 1, '248g.jpg', 1, 'Buku pendamping untuk persiapan Asesmen Kompetensi Minimum (AKM) tingkat SMK/MAK dengan soal dan pembahasan.'),
(249, 'Fisika 3 SMK/MAK Kelas XII Semester 1 dan 2', 'M. Suratman', 'CV AMRICO', 2014, 1, '249g.jpg', 1, 'Buku Fisika kelas XII semester 1 dan 2 untuk SMK/MAK yang membahas teori dan eksperimen fisika lanjutan.'),
(250, 'Sistem Operasi SMK Kelas X', 'Heriyanto, Gito Rolis, Hermas Andrianto', 'Yudhistira', 2014, 1, '250g.jpg', 1, 'Buku pelajaran Sistem Operasi SMK kelas X yang menjelaskan konsep dasar sistem operasi komputer.'),
(251, 'Fisika 1 SMA Kelas X', 'Kamajaya', 'Grafindo Media Pratama', 2013, 1, '251g.jpg', 1, 'Buku Fisika untuk SMA kelas X yang membahas konsep-konsep fisika dasar dengan contoh aplikatif.'),
(252, 'Gambar Teknik Otomotif SMK/MAK Kelas X', 'Wahyu Triyono, Muidu Muif', 'Erlangga', 2018, 1, '252g.jpg', 1, 'Buku Gambar Teknik Otomotif SMK/MAK kelas X yang membahas teknik menggambar komponen otomotif.'),
(253, 'Pekerjaan Dasar Teknik Otomotif SMK/MAK Kelas X', 'Wahyu Triyono', 'Erlangga', 2019, 1, '253g.jpg', 1, 'Buku Pekerjaan Dasar Teknik Otomotif untuk SMK/MAK kelas X yang membahas peralatan dan dasar teknik otomotif.'),
(254, 'Dasar-Dasar Teknik Mesin SMK/MAK Kelas X', 'Agung Widyastara S.Pd., M.Pd., dkk.', 'Erlangga', 2023, 1, '254g.jpg', 1, 'Buku Dasar-Dasar Teknik Mesin SMK/MAK kelas X membahas konsep dasar permesinan dan praktik bengkel.'),
(255, 'Seni Budaya SMA/MA/SMK/MAK Kelas XI Semester 2', 'Sem Cornelyoes Bangun, Siswandi, Tati Narawati, dan Jose Rizal Manua', 'Kementerian Pendidikan dan Kebudayaan Republik Indonesia', 2014, 2, '255g.jpg', 1, 'Buku Seni Budaya SMA/SMK kelas XI semester 2 membahas seni rupa, musik, tari, dan teater.'),
(256, 'Sejarah SMK/MAK Kelas X', 'Ratna Hapsari dan M. Adil', 'Erlangga', 2022, 1, '256g.jpg', 1, 'Buku Sejarah untuk SMK/MAK kelas X yang berfokus pada perjalanan bangsa Indonesia dari masa lampau hingga kini.'),
(257, 'Ilmuwan-Ilmuwan Muslim Pelopor Hebat di Bidang Sains', 'Ehsan Masood', 'PT Gramedia Pustaka Utama', 2009, 1, 'g.jpg', 2, 'Membahas kontribusi para ilmuwan muslim dalam perkembangan ilmu pengetahuan.'),
(258, 'Be a Great Muslimah', 'Febrianti Almeera', 'Mizan', 2013, 1, 'g.jpg', 2, 'Panduan inspiratif untuk wanita muslim agar menjadi pribadi yang lebih baik dan kuat.'),
(259, 'Tuhan, Izinkan Aku Pacaran', 'Fikri Habibullah M', 'Gema Insani', 2010, 1, 'g231.jpg', 2, 'Buku ini merupakan buku motivasi remaja bertema cinta dan pacaran dalam perspektif Islami, yang ditulis untuk membantu generasi muda memahami makna cinta sejati sesuai tuntunan agama. Dengan bahasa yang ringan, jujur, dan menyentuh, penulis mengajak pembaca untuk merenungi hakikat cinta, batas-batas dalam pergaulan, serta cara menjaga kehormatan diri di tengah arus pergaulan modern. Buku ini juga membahas konsep taaruf, komitmen, dan kesiapan menuju pernikahan sebagai alternatif yang lebih bermakna dibandingkan pacaran bebas. Dilengkapi dengan kisah nyata, refleksi, dan nasihat spiritual, buku ini menjadi sahabat yang menuntun remaja agar dapat menyalurkan perasaan cinta secara bijak dan penuh tanggung jawab, tanpa melanggar nilai-nilai Islam.'),
(260, 'Aku Bukan Budak Gaji', 'Ghurmullah Al-Ghamidi', 'Tinta Medina', 2014, 1, 'g.jpg', 2, 'Motivasi untuk meraih kemandirian finansial, keluarga, dan kehidupan sesuai syariat Islam.');
